import React, { useState, useEffect, useCallback } from 'react';

const InventoryTable = () => {
    const [inventory, setInventory] = useState([]);
    const [loading, setLoading] = useState(true);
    const [editingItem, setEditingItem] = useState(null); // Для редактирования
    const [updatedData, setUpdatedData] = useState({}); // Для отслеживания обновленных данных

    // Загрузка данных с сервера
    useEffect(() => {
        const fetchInventory = async () => {
            try {
                const response = await fetch("http://localhost:5000/api/inventory");
                if (!response.ok) {
                    throw new Error(`Ошибка загрузки: ${response.status}`);
                }
                const data = await response.json();
                setInventory(data);
            } catch (error) {
                console.error('Ошибка загрузки инвентаря:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchInventory();
    }, []);

    // Обработчик изменения данных в таблице
    const handleInputChange = (e, field, id) => {
        const value = e.target.value;

        setInventory(prevInventory =>
            prevInventory.map(item =>
                item.id === id ? { ...item, [field]: value } : item
            )
        );

        // Обновляем состояние для отслеживания изменений
        setUpdatedData(prev => {
            const updated = { ...prev };

            // Обновляем старое значение сразу, чтобы следующее сравнение было корректным
            if (!updated[id]) {
                updated[id] = {};
            }

            updated[id][field] = value;

            return updated;
        });
    };

    // Сохранение изменений
const handleSave = async (id) => {
    const updatedItem = inventory.find((item) => item.id === id);
    const updatedFields = updatedData[id] || {};

    // Логируем старые и новые данные перед сравнением
    console.log("Old Item Data:", updatedItem);
    console.log("Updated Fields:", updatedFields);

    const updatedDataForAPI = {};
    let hasChanges = false; // Флаг для отслеживания изменений

    // Сравниваем данные по каждому полю
    Object.keys(updatedFields).forEach((key) => {
        const oldValue = updatedItem[key];
        const newValue = updatedFields[key];

        // Логируем каждое сравнение
        console.log(`Comparing field: ${key}`);
        console.log(`Old value: ${oldValue}`);
        console.log(`New value: ${newValue}`);

        // Преобразуем старое и новое значения в строки (или другие типы данных, если необходимо)
        const oldStrValue = String(oldValue).trim();  // Преобразуем в строку и убираем пробелы
        const newStrValue = String(newValue).trim();

        // Проверяем, если данные изменились
        if (newStrValue !== oldStrValue) {
            updatedDataForAPI[key] = newValue; // Передаем только измененные поля
            hasChanges = true;
        }
    });

    // Логируем данные перед отправкой
    console.log("Sending updated data:", updatedDataForAPI);

    // Если нет изменений, не отправляем
    if (!hasChanges) {
        console.error('Нет изменений для отправки');
        return;  // Прерываем отправку, если данных нет
    }

    try {
        const response = await fetch(`http://localhost:5000/api/inventory/${id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(updatedDataForAPI), // Отправляем только измененные поля
        });

        if (response.ok) {
            alert("Инвентарь обновлен");
            setEditingItem(null); // Закрытие режима редактирования
        } else {
            alert("Ошибка при сохранении изменений");
        }
    } catch (error) {
        console.error('Ошибка при обновлении инвентаря:', error);
    }
};

    // Обработчик клика по строке для начала редактирования
    const handleClickEdit = (itemId) => {
        setEditingItem(itemId); // Устанавливаем редактируемую строку
    };

    // Отображение строки инвентаря
    const renderRow = (item) => {
        if (editingItem === item.id) {
            return (
                <tr key={item.id}>
                    <td>{item.id}</td> {/* ID теперь отображаем только для отладки */}
                    <td>
                        <input
                            type="text"
                            value={item.name}
                            onChange={(e) => handleInputChange(e, 'name', item.id)}
                            onBlur={() => handleSave(item.id)}  // Отправка изменений при завершении редактирования
                        />
                    </td>
                    <td>
                        <input
                            type="text"
                            value={item.category}
                            onChange={(e) => handleInputChange(e, 'category', item.id)}
                            onBlur={() => handleSave(item.id)}  // Отправка изменений при завершении редактирования
                        />
                    </td>
                    <td>
                        <input
                            type="text"
                            value={item.subcategory}
                            onChange={(e) => handleInputChange(e, 'subcategory', item.id)}
                            onBlur={() => handleSave(item.id)}  // Отправка изменений при завершении редактирования
                        />
                    </td>
                    <td>
                        <input
                            type="text"
                            value={item.model}
                            onChange={(e) => handleInputChange(e, 'model', item.id)}
                            onBlur={() => handleSave(item.id)}  // Отправка изменений при завершении редактирования
                        />
                    </td>
                    <td>
                        <input
                            type="text"
                            value={item.serial_number}
                            onChange={(e) => handleInputChange(e, 'serial_number', item.id)}
                            onBlur={() => handleSave(item.id)}  // Отправка изменений при завершении редактирования
                        />
                    </td>
                    <td>
                        <input
                            type="text"
                            value={item.notes}
                            onChange={(e) => handleInputChange(e, 'notes', item.id)}
                            onBlur={() => handleSave(item.id)}  // Отправка изменений при завершении редактирования
                        />
                    </td>
                    <td>
                        <input
                            type="text"
                            value={item.status}
                            onChange={(e) => handleInputChange(e, 'status', item.id)}
                            onBlur={() => handleSave(item.id)}  // Отправка изменений при завершении редактирования
                        />
                    </td>
                    <td>
                        <input
                            type="number"
                            value={item.total}
                            onChange={(e) => handleInputChange(e, 'total', item.id)}
                            onBlur={() => handleSave(item.id)}  // Отправка изменений при завершении редактирования
                        />
                    </td>
                    <td>
                        <input
                            type="text"
                            value={item.belongs_to}
                            onChange={(e) => handleInputChange(e, 'belongs_to', item.id)}
                            onBlur={() => handleSave(item.id)}  // Отправка изменений при завершении редактирования
                        />
                    </td>
                    <td>
                        <button onClick={() => handleSave(item.id)}>Сохранить</button>
                        <button onClick={() => setEditingItem(null)}>Отменить</button>
                    </td>
                </tr>
            );
        } else {
            return (
                <tr key={item.id} onClick={() => handleClickEdit(item.id)}>
                    <td>{item.id}</td>
                    <td>{item.name}</td>
                    <td>{item.category}</td>
                    <td>{item.subcategory}</td>
                    <td>{item.model}</td>
                    <td>{item.serial_number}</td>
                    <td>{item.notes}</td>
                    <td>{item.status}</td>
                    <td>{item.total}</td>
                    <td>{item.belongs_to}</td>
                    <td>
                        <button>Редактировать</button>
                    </td>
                </tr>
            );
        }
    };

    return (
        <div>
            <h2>Инвентарь</h2>
            {loading ? (
                <p>Загрузка...</p>
            ) : (
                <table>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Название</th>
                            <th>Категория</th>
                            <th>Подкатегория</th>
                            <th>Модель</th>
                            <th>Серийный номер</th>
                            <th>Примечания</th>
                            <th>Статус</th>
                            <th>Общее количество</th>
                            <th>Кому принадлежит</th>
                            <th>Действия</th>
                        </tr>
                    </thead>
                    <tbody>
                        {inventory.map(renderRow)}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default InventoryTable;
