import React, { useState, useEffect } from "react";

const Inventory = () => {
    const [inventory, setInventory] = useState([]);
    const [loading, setLoading] = useState(true);
    const [rentedCount, setRentedCount] = useState(0);
    const [sortBy, setSortBy] = useState('name');

    // Загрузка инвентаря с сортировкой
    useEffect(() => {
        fetch(`/api/inventory?sort_by=${sortBy}`)
            .then((res) => res.json()) // Попробуем распарсить JSON
            .then((data) => {
                setInventory(data);
                setLoading(false);
            })
            .catch((error) => {
                console.error('Error fetching inventory:', error);
                setLoading(false);
            });
    }, [sortBy]);

    const handleRentedChange = (e, itemId) => {
        const updatedInventory = inventory.map(item => 
            item.id === itemId ? { ...item, rented: e.target.value } : item
        );
        setInventory(updatedInventory);
        setRentedCount(e.target.value);
    };

    const handleSave = (itemId) => {
        fetch(`/api/inventory/${itemId}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ rented: rentedCount }),
        })
        .then((res) => res.json())
        .then(() => {
            alert("Успешно обновлено");
        });
    };

    return (
        <div>
            <h2>Инвентарь</h2>
            {loading ? (
                <p>Загрузка...</p>
            ) : (
                <>
                    <div>
                        <button onClick={() => setSortBy('name')}>Сортировать по имени</button>
                        <button onClick={() => setSortBy('category')}>Сортировать по категории</button>
                    </div>
                    <table>
                        <thead>
                            <tr>
                                <th>Название</th>
                                <th>Категория</th>
                                <th>Арендовано</th>
                                <th>Обновить</th>
                            </tr>
                        </thead>
                        <tbody>
                            {inventory.map(item => (
                                <tr key={item.id}>
                                    <td>{item.name}</td>
                                    <td>{item.category}</td>
                                    <td>
                                        <input 
                                            type="number" 
                                            value={item.rented}
                                            onChange={(e) => handleRentedChange(e, item.id)} 
                                        />
                                    </td>
                                    <td>
                                        <button onClick={() => handleSave(item.id)}>Сохранить</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </>
            )}
        </div>
    );
};

export default Inventory;
