import React, { useState, useEffect } from "react";

function EquipmentList({ onSelect, onClose }) {
  const [equipmentList, setEquipmentList] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);

  // Запрос к Railway
  useEffect(() => {
    fetch("https://your-railway-url/api/inventory")
      .then((res) => res.json())
      .then((data) => setEquipmentList(data))
      .catch((err) => console.error("Ошибка загрузки оборудования:", err));
  }, []);

  // Обработка выбора
  const toggleSelection = (item) => {
    setSelectedItems((prevSelected) =>
      prevSelected.includes(item)
        ? prevSelected.filter((i) => i !== item)
        : [...prevSelected, item]
    );
  };

  return (
    <div className="modal">
      <h3>Выберите оборудование</h3>
      <ul className="equipment-list">
        {equipmentList.length > 0 ? (
          equipmentList.map((item) => (
            <li key={item.id} className="equipment-item">
              <label>
                <input
                  type="checkbox"
                  checked={selectedItems.includes(item.name)}
                  onChange={() => toggleSelection(item.name)}
                />
                {item.name}{" "}
                {item.rented > 0 && `| Освободится: ${item.availableDate}`} | Доступно:{" "}
                {item.total - item.rented} | Всего: {item.total}
              </label>
            </li>
          ))
        ) : (
          <p>Загрузка...</p>
        )}
      </ul>
      <button onClick={() => onSelect(selectedItems)}>Готово</button>
      <button onClick={onClose}>Отмена</button>
    </div>
  );
}

export default EquipmentList;
