import React, { useState, useEffect, useRef } from "react";
import API_BASE_URL from "../config";

const EquipmentList = ({ onSelect, onClose, selectedEquipment = [] }) => {
    const [equipment, setEquipment] = useState([]);
    const [loading, setLoading] = useState(true);
    const [selectedItems, setSelectedItems] = useState(selectedEquipment);
    const [searchTerm, setSearchTerm] = useState("");
    const listRef = useRef(null);

    useEffect(() => {
        fetch(`${API_BASE_URL}/inventory`)
            .then((res) => {
                if (!res.ok) {
                    throw new Error(`HTTP error! status: ${res.status}`);
                }
                return res.json();
            })
            .then((data) => {
                setEquipment(data);
                setLoading(false);
            })
            .catch((error) => {
                console.error("Ошибка загрузки оборудования:", error);
                setLoading(false);
            });

        const handleClickOutside = (event) => {
            if (listRef.current && !listRef.current.contains(event.target)) {
                onClose();
            }
        };

        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, [onClose]);

    const toggleSelection = (item) => {
        setSelectedItems((prevSelected) => {
            const existingItem = prevSelected.find((selected) => selected.id === item.id);
            if (existingItem) {
                return prevSelected.filter((selected) => selected.id !== item.id);
            } else {
                // Проверяем доступное количество
                const quantity = Math.min(1, item.available);
                return [...prevSelected, { ...item, quantity }];
            }
        });
    };

    const updateQuantity = (itemId, newQuantity, maxAvailable) => {
        // Убедимся, что новое количество не превышает доступное
        const quantity = Math.min(Math.max(1, newQuantity), maxAvailable);
        setSelectedItems((prevSelected) =>
            prevSelected.map((item) =>
                item.id === itemId ? { ...item, quantity } : item
            )
        );
    };

    const handleConfirm = () => {
        // Отправляем только выбранные элементы с их количеством
        onSelect(selectedItems);
        onClose();
    };

    const filteredEquipment = equipment.filter((item) =>
        item.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div className="modal" ref={listRef}>
            <div className="modal-content">
                <h3>Выберите оборудование</h3>
                <input
                    type="text"
                    placeholder="Поиск..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    style={{ width: "100%", marginBottom: "10px", padding: "8px" }}
                />
                {loading ? (
                    <p>Загрузка...</p>
                ) : (
                    <ul style={{ 
                        maxHeight: "300px", 
                        overflowY: "auto",
                        listStyle: "none",
                        padding: 0
                    }}>
                        {filteredEquipment.map((item) => {
                            const selectedItem = selectedItems.find(
                                (selected) => selected.id === item.id
                            );
                            const isAvailable = item.available > 0;

                            return (
                                <li key={item.id} style={{
                                    padding: "8px",
                                    borderBottom: "1px solid #eee",
                                    display: "flex",
                                    alignItems: "center",
                                    justifyContent: "space-between"
                                }}>
                                    <div style={{ display: "flex", alignItems: "center", gap: "8px" }}>
                                        {selectedItem && (
                                            <div style={{ 
                                                display: "flex", 
                                                alignItems: "center",
                                                marginRight: "8px",
                                                border: "1px solid #ccc",
                                                borderRadius: "4px",
                                                overflow: "hidden"
                                            }}>
                                                <button 
                                                    onClick={() => updateQuantity(
                                                        item.id, 
                                                        selectedItem.quantity - 1,
                                                        item.available
                                                    )}
                                                    style={{
                                                        padding: "4px 8px",
                                                        border: "none",
                                                        borderRight: "1px solid #ccc",
                                                        background: "#f5f5f5",
                                                        cursor: "pointer"
                                                    }}
                                                >
                                                    -
                                                </button>
                                                <input
                                                    type="number"
                                                    value={selectedItem.quantity}
                                                    onChange={(e) => updateQuantity(
                                                        item.id,
                                                        parseInt(e.target.value) || 1,
                                                        item.available
                                                    )}
                                                    style={{
                                                        width: "40px",
                                                        textAlign: "center",
                                                        border: "none",
                                                        padding: "4px"
                                                    }}
                                                />
                                                <button 
                                                    onClick={() => updateQuantity(
                                                        item.id,
                                                        selectedItem.quantity + 1,
                                                        item.available
                                                    )}
                                                    style={{
                                                        padding: "4px 8px",
                                                        border: "none",
                                                        borderLeft: "1px solid #ccc",
                                                        background: "#f5f5f5",
                                                        cursor: "pointer"
                                                    }}
                                                >
                                                    +
                                                </button>
                                            </div>
                                        )}
                                        <label style={{ 
                                            display: "flex", 
                                            alignItems: "center",
                                            gap: "8px",
                                            cursor: "pointer"
                                        }}>
                                            <input
                                                type="checkbox"
                                                checked={!!selectedItem}
                                                onChange={() => toggleSelection(item)}
                                                disabled={!isAvailable && !selectedItem}
                                            />
                                            <span>{item.name}</span>
                                        </label>
                                    </div>
                                    <div style={{ color: isAvailable ? "green" : "red" }}>
                                        {isAvailable ? `Доступно: ${item.available}` : "Нет в наличии"}
                                        {item.next_available && 
                                            <div style={{ fontSize: "0.8em", color: "#666" }}>
                                                Освободится: {item.next_available}
                                            </div>
                                        }
                                    </div>
                                </li>
                            );
                        })}
                    </ul>
                )}
                <div style={{ 
                    marginTop: "16px",
                    display: "flex",
                    justifyContent: "flex-end",
                    gap: "8px"
                }}>
                    <button 
                        onClick={onClose}
                        style={{
                            padding: "8px 16px",
                            background: "#f5f5f5",
                            border: "1px solid #ccc",
                            borderRadius: "4px",
                            cursor: "pointer"
                        }}
                    >
                        Отмена
                    </button>
                    <button 
                        onClick={handleConfirm}
                        style={{
                            padding: "8px 16px",
                            background: "#4CAF50",
                            color: "white",
                            border: "none",
                            borderRadius: "4px",
                            cursor: "pointer"
                        }}
                    >
                        Готово
                    </button>
                </div>
            </div>
        </div>
    );
};

export default EquipmentList;
