import React, { useState, useEffect } from "react";
import RentalRow from "./RentalRow";
import API_BASE_URL from "../config";
import './Table.css';

const Table = () => {
    const [bookings, setBookings] = useState([]);
    const [error, setError] = useState(null);
    const [filter, setFilter] = useState('all');

    const fetchBookings = async () => {
        try {
            const response = await fetch(`${API_BASE_URL}/bookings`);
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const data = await response.json();
            
            const sortedData = data.sort((a, b) => {
                if (!a.start_date) return -1;
                if (!b.start_date) return 1;
                return new Date(b.start_date) - new Date(a.start_date);
            });
            
            setBookings(Array.isArray(sortedData) ? sortedData : []);
        } catch (err) {
            setError(err.message);
        }
    };

    useEffect(() => {
        fetchBookings();
    }, []);

    const addRental = () => {
        const newRental = {
            id: Date.now(),
            start_date: "",
            end_date: "",
            renter: "",
            equipment: [],
            issuer: "",
            receiver: "",
            status: "–ë—Ä–æ–Ω—å",
            notes: ""
        };

        setBookings(prevBookings => [newRental, ...prevBookings]);
    };

    const saveRental = async (id, rentalData) => {
        try {
            const formattedRental = {
                ...rentalData,
                start_date: rentalData.start_date ? rentalData.start_date.replace('T', ' ') : null,
                end_date: rentalData.end_date ? rentalData.end_date.replace('T', ' ') : null,
                equipment: Array.isArray(rentalData.equipment) ? rentalData.equipment : [],
                status: rentalData.status || "–ë—Ä–æ–Ω—å" // –£–±–µ–∂–¥–∞–µ–º—Å—è, —á—Ç–æ —Å—Ç–∞—Ç—É—Å —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω
            };
            console.log("API URL:", `${API_BASE_URL}/bookings`);
            const response = await fetch(`${API_BASE_URL}/bookings`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formattedRental)
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.error || `HTTP error! status: ${response.status}`);
            }

            await fetchBookings();
            return true;
        } catch (error) {
            setError(error.message || "–ù–µ —É–¥–∞–ª–æ—Å—å —Å–æ—Ö—Ä–∞–Ω–∏—Ç—å –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ");
            return false;
        }
    };

    const updateRental = async (id, updatedRental) => {
        try {
            if (id.toString().length === 13) {
                // –î–ª—è –≤—Ä–µ–º–µ–Ω–Ω—ã—Ö –∑–∞–ø–∏—Å–µ–π –æ–±–Ω–æ–≤–ª—è–µ–º –ª–æ–∫–∞–ª—å–Ω–æ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ —Å —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ–º —Å—Ç–∞—Ç—É—Å–∞
                setBookings(prevBookings => 
                    prevBookings.map(booking => 
                        booking.id === id ? {
                            ...updatedRental,
                            status: updatedRental.status || booking.status || "–ë—Ä–æ–Ω—å"
                        } : booking
                    )
                );
                return;
            }

            const formattedRental = {
                ...updatedRental,
                start_date: updatedRental.start_date.replace('T', ' '),
                end_date: updatedRental.end_date.replace('T', ' '),
                equipment: Array.isArray(updatedRental.equipment) ? updatedRental.equipment : [],
                status: updatedRental.status || "–ë—Ä–æ–Ω—å" // –£–±–µ–∂–¥–∞–µ–º—Å—è, —á—Ç–æ —Å—Ç–∞—Ç—É—Å —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω
            };

            const response = await fetch(`${API_BASE_URL}/bookings/${id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formattedRental)
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || `HTTP error! status: ${response.status}`);
            }

            await fetchBookings();
        } catch (error) {
            setError(error.message || "–ù–µ —É–¥–∞–ª–æ—Å—å –æ–±–Ω–æ–≤–∏—Ç—å –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ");
        }
    };

    const handleDelete = async (id) => {
        if (!id) return;

        try {
            const response = await fetch(`${API_BASE_URL}/bookings/${id}`, {
                method: 'DELETE',
                headers: {
                    'Content-Type': 'application/json',
                }
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || `HTTP error! status: ${response.status}`);
            }

            await fetchBookings();
        } catch (error) {
            setError(error.message || "–ù–µ —É–¥–∞–ª–æ—Å—å —É–¥–∞–ª–∏—Ç—å –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ");
        }
    };

    const getFilteredBookings = () => {
        switch(filter) {
            case 'active':
                return bookings.filter(booking => 
                    booking.status === "–í—ã–¥–∞–Ω–æ"  // –¢–æ–ª—å–∫–æ –≤—ã–¥–∞–Ω–Ω–æ–µ –æ–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏–µ
                );
            case 'completed':
                return bookings.filter(booking => 
                    booking.status === "–í–æ–∑–≤—Ä–∞—â–µ–Ω–æ"  // –¢–æ–ª—å–∫–æ –≤–æ–∑–≤—Ä–∞—â–µ–Ω–Ω–æ–µ –æ–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏–µ
                );
            case 'all':
            default:
                return bookings;  // –í—Å–µ –∑–∞–ø–∏—Å–∏
        }
    };

    const getStatusCounts = () => ({
        all: bookings.length,
        active: bookings.filter(b => b.status === "–í—ã–¥–∞–Ω–æ").length,
        completed: bookings.filter(b => b.status === "–í–æ–∑–≤—Ä–∞—â–µ–Ω–æ").length
    });

    const counts = getStatusCounts();

    return (
        <div>
            <div className="table-header-container">
                <div className="filters">
                    <button 
                        className={`filter-button ${filter === 'all' ? 'active' : ''}`}
                        onClick={() => setFilter('all')}
                    >
                        –í—Å–µ –±—Ä–æ–Ω–∏ ({counts.all})
                    </button>
                    <button 
                        className={`filter-button ${filter === 'active' ? 'active' : ''}`}
                        onClick={() => setFilter('active')}
                    >
                        –ê–∫—Ç–∏–≤–Ω—ã–µ ({counts.active})
                    </button>
                    <button 
                        className={`filter-button ${filter === 'completed' ? 'active' : ''}`}
                        onClick={() => setFilter('completed')}
                    >
                        –ó–∞–≤–µ—Ä—à—ë–Ω–Ω—ã–µ ({counts.completed})
                    </button>
                </div>
                <button onClick={addRental} className="add-button">
                    ‚ûï –î–æ–±–∞–≤–∏—Ç—å –∞—Ä–µ–Ω–¥—É
                </button>
            </div>
            
            <h2>üìã –¢–∞–±–ª–∏—Ü–∞ –∞—Ä–µ–Ω–¥—ã</h2>
            {error && <p className="error-message">{error}</p>}
            <table className="rental-table">
                <thead>
                    <tr>
                        <th className="table-header">–î–∞—Ç–∞ –∞—Ä–µ–Ω–¥—ã</th>
                        <th className="table-header">–ò–º—è</th>
                        <th className="table-header">–ö—Ç–æ –≤—ã–¥–∞–ª</th>
                        <th className="table-header">–ö—Ç–æ –ø—Ä–∏–Ω—è–ª</th>
                        <th className="table-header">–û–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏–µ</th>
                        <th className="table-header">–ü—Ä–∏–º–µ—á–∞–Ω–∏—è</th>
                        <th className="table-header">–°—Ç–∞—Ç—É—Å</th>
                        <th className="table-header">–ö–æ–Ω—Ñ–ª–∏–∫—Ç—ã</th>
                        <th className="table-header">–î–µ–π—Å—Ç–≤–∏—è</th>
                    </tr>
                </thead>
                <tbody>
                    {getFilteredBookings().map((booking) => (
                        <RentalRow 
                            key={booking.id}
                            booking={booking}
                            onUpdate={updateRental}
                            onSave={saveRental}
                            onDelete={() => handleDelete(booking.id)}
                        />
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Table;
