import React, { useState, useEffect } from "react";
import EquipmentList from "./EquipmentList";
import StatusSelect from "./StatusSelect";
import "./RentalRow.css";

const RentalRow = ({ booking, onUpdate, onDelete }) => {
    const [rental, setRental] = useState({
        ...booking,
        equipment: Array.isArray(booking.equipment) ? booking.equipment : []
    });

    const [showEquipmentList, setShowEquipmentList] = useState(false);
    const [conflicts, setConflicts] = useState({});
    const [showConflictPopup, setShowConflictPopup] = useState(false);

    useEffect(() => {
        setRental({
            ...booking,
            equipment: typeof booking.equipment === "string"
                ? JSON.parse(booking.equipment)  // Если строка → парсим JSON
                : Array.isArray(booking.equipment)
                    ? booking.equipment           // Если уже массив → оставляем
                    : []
        });
    }, [booking]);

    useEffect(() => {
        checkConflicts();
        checkAvailability();
    }, [rental.start_date, rental.end_date, rental.equipment]);

    // Проверка доступности оборудования
    const checkAvailability = async () => {
        if (!rental.start_date || !rental.end_date || rental.equipment.length === 0) return;

        try {
            const response = await fetch("/suggest-availability", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    equipment: rental.equipment,
                    start_date: rental.start_date,
                    end_date: rental.end_date
                })
            });

            const data = await response.json();
            setConflicts(data.conflicts || {});

            if (Object.keys(data.conflicts).length > 0) {
                setShowConflictPopup(true);
            } else {
                setShowConflictPopup(false);
            }
        } catch (error) {
            console.error("Ошибка проверки доступности:", error);
        }
    };

    // Обновление данных
    const handleChange = (e) => {
        const { name, value } = e.target;
        setRental(prev => ({ ...prev, [name]: value }));
    };

    // Сохранение изменений при потере фокуса
    const handleBlur = () => {
        onUpdate(rental.id, rental);
    };

    return (
        <>
            <tr className={Object.keys(conflicts).length > 0 ? "conflict" : ""}>
                {/* Дата аренды */}
                <td>
                    <input
                        type="datetime-local"
                        name="start_date"
                        value={rental.start_date ? new Date(rental.start_date).toISOString().slice(0, 16) : ""}
                        onChange={handleChange}
                        onBlur={handleBlur}
                        step="900"
                    />
                    <input
                        type="datetime-local"
                        name="end_date"
                        value={rental.end_date ? new Date(rental.end_date).toISOString().slice(0, 16) : ""}
                        onChange={handleChange}
                        onBlur={handleBlur}
                        step="900"
                    />
                </td>

                {/* Поля с информацией */}
                <td><input type="text" name="renter" value={rental.renter || ""} onChange={handleChange} onBlur={handleBlur} /></td>
                <td><input type="text" name="issuer" value={rental.issuer || ""} onChange={handleChange} onBlur={handleBlur} /></td>
                <td><input type="text" name="receiver" value={rental.receiver || ""} onChange={handleChange} onBlur={handleBlur} /></td>

                {/* Выбор оборудования */}
                <td>
                    <div className="equipment-container">
                        {rental.equipment?.length > 0 && rental.equipment.map((eq, index) => (
                            <span key={index} className="equipment-tag">
                                {eq.name} × {eq.quantity || 1}
                            </span>
                        ))}
                        <button onClick={() => setShowEquipmentList(true)}>Выбрать оборудование</button>
                    </div>

                    {showEquipmentList && (
                        <EquipmentList
                            selectedEquipment={rental.equipment}
                            onSelect={(selectedEquipment) => {
                                const updatedRental = { ...rental, equipment: selectedEquipment };
                                setRental(updatedRental);
                                onUpdate(rental.id, updatedRental);
                                setShowEquipmentList(false);
                            }}
                            onClose={() => setShowEquipmentList(false)}
                        />
                    )}
                </td>

                {/* Примечания */}
                <td><input type="text" name="notes" value={rental.notes || ""} onChange={handleChange} onBlur={handleBlur} /></td>

                {/* Статус */}
                <td>
                    <StatusSelect
                        value={rental.status}
                        onChange={(status) => {
                            const updatedRental = { ...rental, status };
                            setRental(updatedRental);
                            onUpdate(rental.id, updatedRental);
                        }}
                    />
                </td>

                {/* Конфликты */}
                <td>{Object.keys(conflicts).length > 0 ? "⚠ Конфликт" : "✓"}</td>

                {/* Удаление */}
                <td>
                    <button onClick={() => onDelete(rental.id)}>✕</button>
                </td>
            </tr>

            {/* Всплывающее уведомление при конфликте */}
            {showConflictPopup && (
                <div className="conflict-popup">
                    <p>⚠️ Есть конфликты с оборудованием!</p>
                    {Object.entries(conflicts).map(([name, availableDate]) => (
                        <p key={name}>❌ {name} недоступно, освободится {new Date(availableDate).toLocaleString()}</p>
                    ))}
                    <button onClick={() => setShowConflictPopup(false)}>Закрыть</button>
                </div>
            )}
        </>
    );
};

export default RentalRow;
