import React, { useState, useEffect } from "react";
import RentalRow from "./RentalRow";
import "./styles.css"; // –ï—Å–ª–∏ –Ω—É–∂–µ–Ω —Å—Ç–∏–ª—å

const Table = () => {
    const [bookings, setBookings] = useState([]);
    const [error, setError] = useState(null);

    // –ó–∞–≥—Ä—É–∂–∞–µ–º –¥–∞–Ω–Ω—ã–µ —Å —Å–µ—Ä–≤–µ—Ä–∞
    useEffect(() => {
        const fetchBookings = async () => {
            try {
                const response = await fetch("/api/bookings");
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setBookings(data);
            } catch (err) {
                console.error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏:", err);
                setError(err.message);
            }
        };

        fetchBookings();
    }, []);

    // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –¥–æ–±–∞–≤–ª–µ–Ω–∏—è –Ω–æ–≤–æ–π –∞—Ä–µ–Ω–¥—ã
    const addRental = () => {
        const newRental = {
            id: Date.now(), // –í—Ä–µ–º–µ–Ω–Ω—ã–π ID, –∑–∞–º–µ–Ω–∏—Ç—Å—è –Ω–∞ —Å–µ—Ä–≤–µ—Ä–Ω—ã–π
            start_date: "",
            end_date: "",
            renter: "",
            equipment: "",
            issuer: "",
            receiver: "",
            status: "–ë—Ä–æ–Ω—å",
            notes: ""
        };
        setBookings([...bookings, newRental]);
    };

    return (
        <div>
            <h2>üìã –¢–∞–±–ª–∏—Ü–∞ –∞—Ä–µ–Ω–¥—ã</h2>
            {error && <p className="error">–û—à–∏–±–∫–∞: {error}</p>}
            <button onClick={addRental}>‚ûï –î–æ–±–∞–≤–∏—Ç—å –∞—Ä–µ–Ω–¥—É</button>
            <table>
                <thead>
                    <tr>
                        <th>–î–∞—Ç–∞ –∞—Ä–µ–Ω–¥—ã</th>
                        <th>–ò–º—è</th>
                        <th>–ö—Ç–æ –≤—ã–¥–∞–ª</th>
                        <th>–ö—Ç–æ –ø—Ä–∏–Ω—è–ª</th>
                        <th>–û–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏–µ</th>
                        <th>–ü—Ä–∏–º–µ—á–∞–Ω–∏—è</th>
                        <th>–°—Ç–∞—Ç—É—Å</th>
                        <th>–ö–æ–Ω—Ñ–ª–∏–∫—Ç—ã</th>
                    </tr>
                </thead>
                <tbody>
                    {bookings.map((booking) => (
                        <RentalRow key={booking.id} booking={booking} />
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Table;
