import React, { useState, useEffect } from "react";
import RentalRow from "./RentalRow";
import API_BASE_URL from "../config";
import './Table.css';

const Table = () => {
    const [bookings, setBookings] = useState([]);
    const [error, setError] = useState(null);

    const fetchBookings = async () => {
        try {
            const response = await fetch(`${API_BASE_URL}/bookings`);
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const data = await response.json();
            
            // –°–æ—Ä—Ç–∏—Ä—É–µ–º –∑–∞–ø–∏—Å–∏ –ø–æ –¥–∞—Ç–µ –Ω–∞—á–∞–ª–∞ (–Ω–æ–≤—ã–µ —Å–≤–µ—Ä—Ö—É)
            const sortedData = data.sort((a, b) => {
                if (!a.start_date) return -1;  // –ü—É—Å—Ç—ã–µ –¥–∞—Ç—ã –≤ –Ω–∞—á–∞–ª–æ
                if (!b.start_date) return 1;
                return new Date(b.start_date) - new Date(a.start_date);
            });
            
            setBookings(Array.isArray(data) ? sortedData : []);
        } catch (err) {
            console.error("Error loading data:", err);
            setError(err.message);
        }
    };

    useEffect(() => {
        fetchBookings();
    }, []);

    const addRental = () => {
        const newRental = {
            id: Date.now(),
            start_date: "",
            end_date: "",
            renter: "",
            equipment: [],
            issuer: "",
            receiver: "",
            status: "–ë—Ä–æ–Ω—å",
            notes: ""
        };

        // –î–æ–±–∞–≤–ª—è–µ–º –Ω–æ–≤—É—é –∑–∞–ø–∏—Å—å –≤ –Ω–∞—á–∞–ª–æ —Å–ø–∏—Å–∫–∞
        setBookings(prevBookings => [newRental, ...prevBookings]);
    };

    const updateRental = async (id, updatedRental) => {
        try {
            if (id.toString().length === 13) { // –ü—Ä–æ–≤–µ—Ä–∫–∞ –Ω–∞ –≤—Ä–µ–º–µ–Ω–Ω—ã–π ID
                setBookings(prevBookings => 
                    prevBookings.map(booking => 
                        booking.id === id ? updatedRental : booking
                    )
                );
                return;
            }

            const formattedRental = {
                ...updatedRental,
                start_date: updatedRental.start_date.replace('T', ' '),
                end_date: updatedRental.end_date.replace('T', ' '),
                equipment: Array.isArray(updatedRental.equipment) ? updatedRental.equipment : []
            };

            const response = await fetch(`${API_BASE_URL}/bookings/${id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formattedRental)
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || `HTTP error! status: ${response.status}`);
            }

            await fetchBookings();  // –û–±–Ω–æ–≤–ª—è–µ–º –∏ —Å–æ—Ä—Ç–∏—Ä—É–µ–º —Å–ø–∏—Å–æ–∫ –≤—Å–µ—Ö –∑–∞–ø–∏—Å–µ–π
        } catch (error) {
            console.error("–û—à–∏–±–∫–∞ –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è:", error);
            setError(error.message || "–ù–µ —É–¥–∞–ª–æ—Å—å –æ–±–Ω–æ–≤–∏—Ç—å –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ");
        }
    };

    const handleDelete = async (id) => {
        if (!id) {
            console.error("ID –Ω–µ —É–∫–∞–∑–∞–Ω –¥–ª—è —É–¥–∞–ª–µ–Ω–∏—è");
            return;
        }

        try {
            const response = await fetch(`${API_BASE_URL}/bookings/${id}`, {
                method: 'DELETE',
                headers: {
                    'Content-Type': 'application/json',
                }
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || `HTTP error! status: ${response.status}`);
            }

            await fetchBookings();  // –û–±–Ω–æ–≤–ª—è–µ–º –∏ —Å–æ—Ä—Ç–∏—Ä—É–µ–º —Å–ø–∏—Å–æ–∫ –≤—Å–µ—Ö –∑–∞–ø–∏—Å–µ–π
        } catch (error) {
            console.error("–û—à–∏–±–∫–∞ —É–¥–∞–ª–µ–Ω–∏—è:", error);
            setError(error.message || "–ù–µ —É–¥–∞–ª–æ—Å—å —É–¥–∞–ª–∏—Ç—å –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ");
        }
    };

    return (
        <div>
            <h2>üìã –¢–∞–±–ª–∏—Ü–∞ –∞—Ä–µ–Ω–¥—ã</h2>
            {error && <p className="error-message">{error}</p>}
            <button onClick={addRental} className="add-button">‚ûï –î–æ–±–∞–≤–∏—Ç—å –∞—Ä–µ–Ω–¥—É</button>
            <table className="rental-table">
                <thead>
                    <tr>
                        <th className="table-header">–î–∞—Ç–∞ –∞—Ä–µ–Ω–¥—ã</th>
                        <th className="table-header">–ò–º—è</th>
                        <th className="table-header">–ö—Ç–æ –≤—ã–¥–∞–ª</th>
                        <th className="table-header">–ö—Ç–æ –ø—Ä–∏–Ω—è–ª</th>
                        <th className="table-header">–û–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏–µ</th>
                        <th className="table-header">–ü—Ä–∏–º–µ—á–∞–Ω–∏—è</th>
                        <th className="table-header">–°—Ç–∞—Ç—É—Å</th>
                        <th className="table-header">–ö–æ–Ω—Ñ–ª–∏–∫—Ç—ã</th>
                        <th className="table-header">–î–µ–π—Å—Ç–≤–∏—è</th>
                    </tr>
                </thead>
                <tbody>
                    {bookings.map((booking) => (
                        <RentalRow 
                            key={booking.id}
                            booking={booking}
                            onUpdate={updateRental}
                            onDelete={() => handleDelete(booking.id)}
                        />
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Table;
