import React, { useState, useEffect } from "react";
import EquipmentList from "./EquipmentList";
import StatusSelect from "./StatusSelect";
import './RentalRow.css';

const RentalRow = ({ booking, onUpdate, onSave, onDelete }) => {
    const [rental, setRental] = useState({
        ...booking,
        equipment: Array.isArray(booking.equipment) ? booking.equipment : []
    });
    const [showEquipmentList, setShowEquipmentList] = useState(false);
    const [conflicts, setConflicts] = useState([]);

    useEffect(() => {
        setRental({
            ...booking,
            equipment: Array.isArray(booking.equipment) ? booking.equipment : []
        });
    }, [booking]);

    useEffect(() => {
        checkConflicts();
    }, [rental.start_date, rental.end_date, rental.status]);

    const checkConflicts = () => {
        // ... (оставляем как есть)
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setRental(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleBlur = (e) => {
        const { name } = e.target;
        if (onUpdate && rental.id.toString().length !== 13) {
            onUpdate(rental.id, rental);
        }
    };

    const handleSave = async () => {
        if (onSave) {
            const success = await onSave(rental.id, rental);
            if (success) {
                console.log("Бронирование успешно сохранено");
            }
        }
    };

    return (
        <tr>
            <td className="rental-date-cell">
                <input
                    type="datetime-local"
                    name="start_date"
                    value={rental.start_date ? rental.start_date.slice(0, 16) : ""}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    step="900"
                    className="rental-date-input"
                />
                <span className="rental-date-separator">-</span>
                <input
                    type="datetime-local"
                    name="end_date"
                    value={rental.end_date ? rental.end_date.slice(0, 16) : ""}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    step="900"
                    className="rental-date-input"
                />
            </td>

            <td>
                <input 
                    type="text" 
                    name="renter" 
                    value={rental.renter || ''} 
                    onChange={handleChange}
                    onBlur={handleBlur}
                />
            </td>

            <td>
                <input 
                    type="text" 
                    name="issuer" 
                    value={rental.issuer || ''} 
                    onChange={handleChange}
                    onBlur={handleBlur}
                />
            </td>

            <td>
                <input 
                    type="text" 
                    name="receiver" 
                    value={rental.receiver || ''} 
                    onChange={handleChange}
                    onBlur={handleBlur}
                />
            </td>

            <td>
                <button 
                    onClick={() => setShowEquipmentList(true)} 
                    className="equipment-button"
                >
                    {rental.equipment && Array.isArray(rental.equipment) && rental.equipment.length > 0
                        ? rental.equipment.map((eq) => (
                            <span
                                key={eq.id}
                                className="equipment-tag"
                            >
                                {eq.name} × {eq.quantity || 1}
                            </span>
                          ))
                        : "Выбрать оборудование"}
                </button>
                {showEquipmentList && (
                    <EquipmentList
                        selectedEquipment={Array.isArray(rental.equipment) ? rental.equipment : []}
                        onSelect={(selectedEquipment) => {
                            const updatedRental = { ...rental, equipment: selectedEquipment };
                            setRental(updatedRental);
                            if (onUpdate && rental.id.toString().length !== 13) {
                                onUpdate(rental.id, updatedRental);
                            }
                            setShowEquipmentList(false);
                        }}
                        onClose={() => setShowEquipmentList(false)}
                    />
                )}
            </td>

            <td>
                <input 
                    type="text" 
                    name="notes" 
                    value={rental.notes || ''} 
                    onChange={handleChange}
                    onBlur={handleBlur}
                />
            </td>

            <td>
                <StatusSelect
                    value={rental.status}
                    onChange={(status) => {
                        const updatedRental = { ...rental, status };
                        setRental(updatedRental);
                        if (onUpdate && rental.id.toString().length !== 13) {
                            onUpdate(rental.id, updatedRental);
                        }
                    }}
                />
            </td>

            {/* ... остальной код остается без изменений ... */}
        </tr>
    );
};

export default RentalRow;
