import React, { useState } from "react";
import RentalRow from "./RentalRow";

function Table() {
  const [rentals, setRentals] = useState([]);

  // Добавление новой аренды
  const addRental = () => {
    setRentals([
      ...rentals,
      {
        id: Date.now(),
        name: "",
        equipment: [],
        rentalDate: "",
        issuedBy: "",
        receivedBy: "",
        notes: "",
        status: "Бронь",
        conflict: ""
      }
    ]);
  };

  // Обновление аренды
  const updateRental = (id, updatedRental) => {
    setRentals(rentals.map((rental) => (rental.id === id ? updatedRental : rental)));
  };

  return (
    <div>
      <h2>Таблица аренды оборудования</h2>
      <button onClick={addRental}>Добавить аренду</button>
      <table>
        <thead>
          <tr>
            <th>Имя</th>
            <th>Оборудование</th>
            <th>Дата аренды</th>
            <th>Кто выдал</th>
            <th>Кто принял</th>
            <th>Примечания</th>
            <th>Статус</th>
            <th>Конфликты</th>
          </tr>
        </thead>
        <tbody>
          {rentals.map((rental) => (
            <RentalRow key={rental.id} rental={rental} updateRental={updateRental} />
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Table;
