import React, { useState } from "react";
import RentalsTable from "./RentalsTable";
import EquipmentSelect from "./EquipmentSelect";
import useRentals from "./hooks/useRentals";
import "./styles.css";

function Table({ debugMode }) {
  const { rentals, addRental, updateEquipment, equipmentList, selectedRow, setSelectedRow } = useRentals();

  return (
    <div className="table-container">
      <button onClick={addRental}>+ Добавить аренду</button>
      <RentalsTable rentals={rentals} updateEquipment={updateEquipment} debugMode={debugMode} setSelectedRow={setSelectedRow} />
      
      {selectedRow !== null && (
        <EquipmentSelect
          selectedEquipment={rentals[selectedRow]?.equipment || []}
          onSelect={(equipment) => {
            updateEquipment(selectedRow, equipment);
            setSelectedRow(null);
          }}
          equipmentList={equipmentList}
        />
      )}
    </div>
  );
}

export default Table;
