import React, { useState, useEffect } from "react";
import RentalRow from "./RentalRow";
import "./styles.css"; // If styling is needed

const Table = () => {
    const [bookings, setBookings] = useState([]);
    const [error, setError] = useState(null);

    // Load data from server
    useEffect(() => {
        const fetchBookings = async () => {
            try {
                const response = await fetch("/api/bookings");
                if (!response.ok) {
                    // Check for specific HTTP errors
                    if (response.status === 404) {
                        throw new Error("Resource not found (404)");
                    } else if (response.status === 500) {
                        throw new Error("Internal server error (500)");
                    } else {
                        throw new Error(`HTTP error! status: ${response.status}`);
                    }
                }
                const data = await response.json();
                setBookings(data);
            } catch (err) {
                console.error("Error loading data:", err);
                setError(err.message);
            }
        };

        fetchBookings();
    }, []);

    // Function to add a new rental
    const addRental = () => {
        const newRental = {
            id: Date.now(), // Temporary ID, will be replaced by server
            start_date: "",
            end_date: "",
            renter: "",
            equipment: "",
            issuer: "",
            receiver: "",
            status: "Reserved",
            notes: ""
        };
        setBookings([...bookings, newRental]);
    };

    return (
        <div>
            <h2>ðŸ“‹ Rental Table</h2>
            {error && <p className="error">Error: {error}</p>}
            <button onClick={addRental}>âž• Add Rental</button>
            <table>
                <thead>
                    <tr>
                        <th>Rental Date</th>
                        <th>Name</th>
                        <th>Issuer</th>
                        <th>Receiver</th>
                        <th>Equipment</th>
                        <th>Notes</th>
                        <th>Status</th>
                        <th>Conflicts</th>
                    </tr>
                </thead>
                <tbody>
                    {bookings.map((booking) => (
                        <RentalRow key={booking.id} booking={booking} />
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Table;
