import React, { useState } from "react";
import EquipmentList from "./EquipmentList";
import StatusSelect from "./StatusSelect";

const RentalRow = ({ booking, onUpdate, onDelete }) => {
    const [rental, setRental] = useState(booking);
    const [showEquipmentList, setShowEquipmentList] = useState(false);

    // Обновление значений аренды
    const handleChange = (e) => {
        const updatedRental = { ...rental, [e.target.name]: e.target.value };
        setRental(updatedRental);
        onUpdate(rental.id, updatedRental);
    };

    return (
        <>
            <tr>
                {/* Даты аренды */}
                <td>
                    <input
                        type="datetime-local"
                        name="start_date"
                        value={rental.start_date}
                        onChange={handleChange}
                    />
                    {" - "}
                    <input
                        type="datetime-local"
                        name="end_date"
                        value={rental.end_date}
                        onChange={handleChange}
                    />
                </td>

                {/* Поля ввода */}
                <td><input type="text" name="renter" value={rental.renter} onChange={handleChange} /></td>
                <td><input type="text" name="issuer" value={rental.issuer} onChange={handleChange} /></td>
                <td><input type="text" name="receiver" value={rental.receiver} onChange={handleChange} /></td>

                {/* Кнопка выбора оборудования */}
                <td>
                    <button onClick={() => setShowEquipmentList(true)}>
                        {rental.equipment ? rental.equipment : "Выбрать оборудование"}
                    </button>
                </td>

                <td><input type="text" name="notes" value={rental.notes} onChange={handleChange} /></td>

                {/* Выбор статуса */}
                <td>
                    <StatusSelect value={rental.status} onChange={(status) => {
                        const updatedRental = { ...rental, status };
                        setRental(updatedRental);
                        onUpdate(rental.id, updatedRental);
                    }} />
                </td>

                {/* Колонка "Конфликты" (позже добавим логику) */}
                <td>⚠️</td>

                {/* Кнопка удаления */}
                <td>
                    <button onClick={() => onDelete(rental.id)}>❌</button>
                </td>
            </tr>

            {/* Всплывающий список оборудования */}
            {showEquipmentList && (
                <EquipmentList
                    onSelect={(equipment) => {
                        const updatedRental = { ...rental, equipment };
                        setRental(updatedRental);
                        onUpdate(rental.id, updatedRental);
                        setShowEquipmentList(false);
                    }}
                    onClose={() => setShowEquipmentList(false)}
                />
            )}
        </>
    );
};

export default RentalRow;
