import React, { useState, useEffect, useRef } from "react";
import ReactDOM from "react-dom";
import API_BASE_URL from "../config";
import "./EquipmentList.css";

const modalRoot = document.getElementById("modal-root") || (() => {
    const el = document.createElement("div");
    el.id = "modal-root";
    document.body.appendChild(el);
    return el;
})();

const EquipmentList = ({ onSelect, onClose, selectedEquipment, startDate, endDate }) => { 
    const listRef = useRef(null);
    const [equipment, setEquipment] = useState([]);
    const [loading, setLoading] = useState(true);
    const [selectedItems, setSelectedItems] = useState(Array.isArray(selectedEquipment) ? selectedEquipment : []);
    const [searchTerm, setSearchTerm] = useState("");
    const [expandedCategories, setExpandedCategories] = useState({});

    useEffect(() => {
        const fetchEquipment = async () => {
            try {
                const params = new URLSearchParams();
                if (startDate) params.append("start_date", startDate);
                if (endDate) params.append("end_date", endDate);
    
                const response = await fetch(`${API_BASE_URL}/inventory?${params.toString()}`);
                if (!response.ok) {
                    throw new Error(`–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏: ${response.status}`);
                }
                const data = await response.json();
                console.log("–ó–∞–≥—Ä—É–∂–µ–Ω–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ:", data);  // üü¢ –õ–û–ì
    
                setEquipment(data);
            } catch (error) {
                console.error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –æ–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏—è:", error);
            } finally {
                setLoading(false);
            }
        };
    
        fetchEquipment();
    

        const handleClickOutside = (event) => {
            if (listRef.current && !listRef.current.contains(event.target)) {
                onClose();
            }
        };

        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, [onClose]);

    const toggleSelection = (item) => {
        setSelectedItems((prevSelected) => {
            if (!Array.isArray(prevSelected)) prevSelected = [];

            const existingItem = prevSelected.find((selected) => selected.id === item.id);
            if (existingItem) {
                return prevSelected.filter((selected) => selected.id !== item.id);
            } else {
                return [...prevSelected, { ...item, quantity: 1 }];
            }
        });
    };

    const updateQuantity = (itemId, newQuantity) => {
        setSelectedItems((prevSelected) => {
            return prevSelected.map((item) =>
                item.id === itemId
                    ? { ...item, quantity: Math.min(Math.max(1, newQuantity || 1), item.quantity) }
                    : item
            );
        });
    };

    const handleConfirm = () => {
        onSelect(Array.isArray(selectedItems) ? selectedItems : []);
        onClose();
    };

// –°–æ–∑–¥–∞–µ–º –æ–±—ä–µ–∫—Ç –¥–ª—è –Ω–∞—Ç—É—Ä–∞–ª—å–Ω–æ–π —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫–∏
const collator = new Intl.Collator("ru", { numeric: true, sensitivity: "base" });

// –ì—Ä—É–ø–ø–∏—Ä—É–µ–º –æ–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏–µ –ø–æ –∫–∞—Ç–µ–≥–æ—Ä–∏—è–º –∏ —Å–æ—Ä—Ç–∏—Ä—É–µ–º –ø–æ –∞–ª—Ñ–∞–≤–∏—Ç—É —Å —É—á–µ—Ç–æ–º —á–∏—Å–µ–ª
const groupedEquipment = equipment.reduce((acc, item) => {
    if (!acc[item.category]) acc[item.category] = [];
    acc[item.category].push(item);
    return acc;
}, {});

// –°–æ—Ä—Ç–∏—Ä—É–µ–º –æ–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏–µ –≤ –∫–∞–∂–¥–æ–π –∫–∞—Ç–µ–≥–æ—Ä–∏–∏
Object.keys(groupedEquipment).forEach((category) => {
    groupedEquipment[category].sort((a, b) => collator.compare(a.name, b.name));
});

    const toggleCategory = (category) => {
        setExpandedCategories((prev) => ({
            ...prev,
            [category]: !prev[category],
        }));
    };

    const filteredEquipment = equipment
    .filter((item) => item.name.toLowerCase().includes(searchTerm.toLowerCase()))
    .sort((a, b) => collator.compare(a.name, b.name));

const isSearching = searchTerm.trim().length > 0;

return ReactDOM.createPortal(
    <div className="equipment-modal" ref={listRef}>
        <div className="equipment-modal-content">
            <h3>–í—ã–±–µ—Ä–∏—Ç–µ –æ–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏–µ</h3>
            <input
                type="text"
                placeholder="–ü–æ–∏—Å–∫..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="equipment-search"
            />
            {loading ? (
                <p>–ó–∞–≥—Ä—É–∑–∫–∞...</p>
            ) : (
                <div className="equipment-list">
                    {isSearching ? (
                        filteredEquipment.map((item) => {
                            const selectedItem = selectedItems.find((selected) => selected.id === item.id);
                            const isAvailable = (item.quantity - (item.rented || 0)) > 0;

                            return (
                                <li key={item.id} className="equipment-item">
                                    <div className="equipment-controls">
                                        {selectedItem && (
                                            <div className="quantity-control">
                                                <button
                                                    onClick={(e) => {
                                                        e.stopPropagation();
                                                        updateQuantity(item.id, selectedItem.quantity - 1);
                                                    }}
                                                    className="quantity-button"
                                                    disabled={selectedItem.quantity <= 1}
                                                >
                                                    -
                                                </button>
                                                <input
                                                    type="number"
                                                    value={selectedItem.quantity}
                                                    onChange={(e) =>
                                                        updateQuantity(item.id, parseInt(e.target.value) || 1)
                                                    }
                                                    className="quantity-input"
                                                    min="1"
                                                    max={item.quantity}
                                                />
                                                <button
                                                    onClick={(e) => {
                                                        e.stopPropagation();
                                                        updateQuantity(item.id, selectedItem.quantity + 1);
                                                    }}
                                                    className="quantity-button"
                                                    disabled={selectedItem.quantity >= item.quantity}
                                                >
                                                    +
                                                </button>
                                            </div>
                                        )}
                                        <label className="equipment-label">
                                            <input
                                                type="checkbox"
                                                checked={!!selectedItem}
                                                onChange={() => toggleSelection(item)}
                                            />
                                            <span>{item.name}</span>
                                        </label>
                                    </div>
                                    <div className={`availability ${isAvailable ? "available" : "unavailable"}`}>
                                        {isAvailable ? `–î–æ—Å—Ç—É–ø–Ω–æ: ${item.quantity - (item.rented || 0)}` : "–ù–µ—Ç –≤ –Ω–∞–ª–∏—á–∏–∏"}
                                    </div>
                                </li>
                            );
                        })
                    ) : (
                        Object.entries(groupedEquipment).map(([category, items]) => (
                            <div key={category} className="equipment-category">
                                <button className="category-toggle" onClick={() => toggleCategory(category)}>
                                    {expandedCategories[category] ? "‚ñº" : "‚ñ∂"} {category}
                                </button>
                                {expandedCategories[category] && (
                                    <ul>
                                        {items.map((item) => {
                                            const selectedItem = selectedItems.find((selected) => selected.id === item.id);
                                            const isAvailable = (item.quantity - (item.rented || 0)) > 0;

                                            return (
                                                <li key={item.id} className="equipment-item">
                                                    <div className="equipment-controls">
                                                        {selectedItem && (
                                                            <div className="quantity-control">
                                                                <button
                                                                    onClick={(e) => {
                                                                        e.stopPropagation();
                                                                        updateQuantity(item.id, selectedItem.quantity - 1);
                                                                    }}
                                                                    className="quantity-button"
                                                                    disabled={selectedItem.quantity <= 1}
                                                                >
                                                                    -
                                                                </button>
                                                                <input
                                                                    type="number"
                                                                    value={selectedItem.quantity}
                                                                    onChange={(e) =>
                                                                        updateQuantity(item.id, parseInt(e.target.value) || 1)
                                                                    }
                                                                    className="quantity-input"
                                                                    min="1"
                                                                    max={item.quantity}
                                                                />
                                                                <button
                                                                    onClick={(e) => {
                                                                        e.stopPropagation();
                                                                        updateQuantity(item.id, selectedItem.quantity + 1);
                                                                    }}
                                                                    className="quantity-button"
                                                                    disabled={selectedItem.quantity >= item.quantity}
                                                                >
                                                                    +
                                                                </button>
                                                            </div>
                                                        )}
                                                        <label className="equipment-label">
                                                            <input
                                                                type="checkbox"
                                                                checked={!!selectedItem}
                                                                onChange={() => toggleSelection(item)}
                                                            />
                                                            <span>{item.name}</span>
                                                        </label>
                                                    </div>
                                                    <div className={`availability ${isAvailable ? "available" : "unavailable"}`}>
