import React, { useState } from "react";
import Sidebar from "./components/Sidebar";
import Table from "./components/Table";
import DebugToggle from "./components/DebugToggle"; // Теперь он есть!
import "./components/styles.css";

const App = () => {
    const [isShifted, setIsShifted] = useState(false);
    const [debugMode, setDebugMode] = useState(false);

    const toggleDebug = () => {
        setDebugMode(!debugMode);
    };

    return (
        <div className={`content ${isShifted ? "shifted" : ""}`}>
            <Sidebar toggleShift={setIsShifted} />

            {/* Кнопка DebugToggle */}
            <DebugToggle toggleDebug={toggleDebug} />

            {/* Передаем debugMode в Table */}
            <Table debugMode={debugMode} />
        </div>
    );
};

export default App;
