import React, { useState } from "react";
import Calendar from "./Calendar";
import EquipmentList from "./EquipmentList";
import StatusSelect from "./StatusSelect";
import "./styles.css";

function Table() {
  const [rentals, setRentals] = useState([]);
  const [selectedRow, setSelectedRow] = useState(null);
  const [calendarPosition, setCalendarPosition] = useState({ top: 0, left: 0 });

  const addRental = () => {
    setRentals([
      ...rentals,
      {
        id: rentals.length + 1,
        date: "",
        renter: "",
        equipment: "",
        issuer: "",
        receiver: "",
        status: "Бронь",
        notes: "",
      },
    ]);
  };

  const handleDateClick = (index, event) => {
    setSelectedRow(index);
    const rect = event.target.getBoundingClientRect();
    setCalendarPosition({
      top: rect.bottom + window.scrollY + 5,
      left: rect.left + window.scrollX,
    });
  };

  const handleDateSelect = (rowId, date) => {
    let newRentals = [...rentals];
    newRentals[rowId].date = date.toLocaleDateString();
    setRentals(newRentals);
    setSelectedRow(null); // Закрываем календарь после выбора
  };

  return (
    <div className="table-container">
      <button onClick={addRental}>+ Добавить аренду</button>
      <table>
        <thead>
          <tr>
            <th>Дата аренды</th>
            <th>Кто берет</th>
            <th>Оборудование</th>
            <th>Кто выдал</th>
            <th>Кто принял</th>
            <th>Статус</th>
            <th>Примечания</th>
          </tr>
        </thead>
        <tbody>
          {rentals.map((rental, index) => (
            <tr key={index}>
              <td>
                <button
                  className="date-button"
                  onClick={(e) => handleDateClick(index, e)}
                >
                  {rental.date || "Выбрать дату"}
                </button>
              </td>
              <td>
                <input
                  type="text"
                  value={rental.renter}
                  onChange={(e) => {
                    let newRentals = [...rentals];
                    newRentals[index].renter = e.target.value;
                    setRentals(newRentals);
                  }}
                />
              </td>
              <td onClick={() => alert("Открыть список оборудования")}>
                {rental.equipment || "Выбрать"}
              </td>
              <td>
                <input
                  type="text"
                  value={rental.issuer}
                  onChange={(e) => {
                    let newRentals = [...rentals];
                    newRentals[index].issuer = e.target.value;
                    setRentals(newRentals);
                  }}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={rental.receiver}
                  onChange={(e) => {
                    let newRentals = [...rentals];
                    newRentals[index].receiver = e.target.value;
                    setRentals(newRentals);
                  }}
                />
              </td>
              <td>
                <StatusSelect
                  status={rental.status}
                  onChange={(status) => {
                    let newRentals = [...rentals];
                    newRentals[index].status = status;
                    setRentals(newRentals);
                  }}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={rental.notes}
                  onChange={(e) => {
                    let newRentals = [...rentals];
                    newRentals[index].notes = e.target.value;
                    setRentals(newRentals);
                  }}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {selectedRow !== null && (
        <div
          className="calendar-popup"
          style={{ top: `${calendarPosition.top}px`, left: `${calendarPosition.left}px` }}
        >
          <Calendar onDateSelect={(date) => handleDateSelect(selectedRow, date)} />
        </div>
      )}
    </div>
  );
}

export default Table;
