import React, { useState, useEffect } from "react";
import Calendar from "./Calendar";
import StatusSelect from "./StatusSelect";
import "./styles.css";

function Table() {
  const [rentals, setRentals] = useState([]);
  const [selectedRow, setSelectedRow] = useState(null);
  const [equipmentList, setEquipmentList] = useState([]);
  const [equipmentModal, setEquipmentModal] = useState(null);

  useEffect(() => {
    fetch("/equipment")
      .then((res) => res.json())
      .then((data) => setEquipmentList(data))
      .catch((err) => console.error("Ошибка загрузки оборудования:", err));
  }, []);

  const addRental = () => {
    setRentals([
      ...rentals,
      {
        id: rentals.length + 1,
        dateStart: "",
        dateEnd: "",
        renter: "",
        equipment: "",
        issuer: "",
        receiver: "",
        status: "Бронь",
        notes: "",
      },
    ]);
  };

  const handleDateSelect = (rowId, date, type) => {
    if (!date) return;

    let newRentals = [...rentals];
    newRentals[rowId][type] = date.toLocaleString("ru-RU", {
      day: "2-digit",
      month: "2-digit",
      year: "2-digit",
      hour: "2-digit",
      minute: "2-digit",
    });
    setRentals(newRentals);
    setSelectedRow(null);
  };

  const selectEquipment = (rowId) => {
    setEquipmentModal(rowId);
  };

  const handleEquipmentSelect = (rowId, equipment) => {
    let newRentals = [...rentals];
    newRentals[rowId].equipment = equipment.name;
    setRentals(newRentals);
    setEquipmentModal(null);

    fetch("/update_rental", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ id: equipment.id, rented: true }),
    }).catch((err) => console.error("Ошибка обновления аренды:", err));
  };

  return (
    <div className="table-container">
      <button onClick={addRental}>+ Добавить аренду</button>
      <table>
        <thead>
          <tr>
            <th>Дата аренды</th>
            <th>Кто берет</th>
            <th>Оборудование</th>
            <th>Кто выдал</th>
            <th>Кто принял</th>
            <th>Статус</th>
            <th>Примечания</th>
          </tr>
        </thead>
        <tbody>
          {rentals.map((rental, index) => (
            <tr key={index}>
              <td className="date-cell">
                <div onClick={() => setSelectedRow({ index, type: "dateStart" })}>
                  {rental.dateStart || "С: Выбрать"}
                </div>
                <div onClick={() => setSelectedRow({ index, type: "dateEnd" })}>
                  {rental.dateEnd || "По: Выбрать"}
                </div>
              </td>
              <td>
                <input
                  type="text"
                  value={rental.renter}
                  onChange={(e) => {
                    let newRentals = [...rentals];
                    newRentals[index].renter = e.target.value;
                    setRentals(newRentals);
                  }}
                />
              </td>
              <td onClick={() => selectEquipment(index)}>
                {rental.equipment || "Выбрать"}
              </td>
              <td>
                <input
                  type="text"
                  value={rental.issuer}
                  onChange={(e) => {
                    let newRentals = [...rentals];
                    newRentals[index].issuer = e.target.value;
                    setRentals(newRentals);
                  }}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={rental.receiver}
                  onChange={(e) => {
                    let newRentals = [...rentals];
                    newRentals[index].receiver = e.target.value;
                    setRentals(newRentals);
                  }}
                />
              </td>
              <td>
                <StatusSelect
                  status={rental.status}
                  onChange={(status) => {
                    let newRentals = [...rentals];
                    newRentals[index].status = status;
                    setRentals(newRentals);
                  }}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={rental.notes}
                  onChange={(e) => {
                    let newRentals = [...rentals];
                    newRentals[index].notes = e.target.value;
                    setRentals(newRentals);
                  }}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {selectedRow !== null && (
        <div className="calendar-popup">
          <Calendar
            onClose={() => setSelectedRow(null)}
            onDateSelect={(date) => handleDateSelect(selectedRow.index, date, selectedRow.type)}
          />
        </div>
      )}

      {equipmentModal !== null && (
        <div className="modal">
          <h3>Выберите оборудование</h3>
          <ul>
            {equipmentList.map((item) => (
              <li key={item.id} onClick={() => handleEquipmentSelect(equipmentModal, item)}>
                {item.name}
              </li>
            ))}
          </ul>
          <button onClick={() => setEquipmentModal(null)}>Закрыть</button>
        </div>
      )}
    </div>
  );
}

export default Table;
