import React, { useEffect, useState, useRef } from "react";
import API_BASE_URL from "../config"; // Импортируем базовый URL

const EquipmentList = ({ onSelect, onClose }) => {
    const [equipment, setEquipment] = useState([]);
    const [loading, setLoading] = useState(true);
    const [selectedItems, setSelectedItems] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [expandedCategories, setExpandedCategories] = useState({});
    const listRef = useRef(null);

    useEffect(() => {
        fetch(`${API_BASE_URL}/inventory`)
            .then((res) => res.json())
            .then((data) => {
                setEquipment(data);
                setLoading(false);
            })
            .catch((error) => {
                console.error("Ошибка загрузки оборудования:", error);
                setLoading(false);
            });

        const handleClickOutside = (event) => {
            if (listRef.current && !listRef.current.contains(event.target)) {
                onClose();
            }
        };

        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, [onClose]);

    const toggleSelection = (item) => {
        setSelectedItems((prevSelected) =>
            prevSelected.some((selected) => selected.id === item.id)
                ? prevSelected.filter((selected) => selected.id !== item.id)
                : [...prevSelected, item]
        );
    };

    const handleConfirm = () => {
        onSelect(selectedItems);
        onClose();
    };

    const toggleCategory = (category) => {
        setExpandedCategories((prev) => ({
            ...prev,
            [category]: !prev[category],
        }));
    };

    const filteredEquipment = equipment.filter((item) =>
        item.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    const categorizedEquipment = filteredEquipment.reduce((acc, item) => {
        const category = item.category || "Без категории";
        if (!acc[category]) {
            acc[category] = [];
        }
        acc[category].push(item);
        return acc;
    }, {});

    return (
        <div className="modal" ref={listRef}>
            <div className="modal-content">
                <h3>Выберите оборудование</h3>
                <input
                    type="text"
                    placeholder="Поиск..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    style={{ width: "100%", marginBottom: "10px" }}
                />
                {loading ? (
                    <p>Загрузка...</p>
                ) : (
                    <ul style={{ maxHeight: "300px", overflowY: "auto" }}>
                        {Object.entries(categorizedEquipment).map(([category, items]) => (
                            <li key={category}>
                                <div onClick={() => toggleCategory(category)} style={{ cursor: "pointer", fontWeight: "bold" }}>
                                    {category} ({items.length})
                                </div>
                                {expandedCategories[category] && (
                                    <ul>
                                        {items.map((item) => (
                                            <li key={item.id}>
                                                <label>
                                                    <input
                                                        type="checkbox"
                                                        checked={selectedItems.some((selected) => selected.id === item.id)}
                                                        onChange={() => toggleSelection(item)}
                                                    />
                                                    {item.name} | {item.available > 0 ? `Доступно: ${item.available}` : "❌ Нет в наличии"}
                                                    {item.next_available && ` | Освободится: ${item.next_available}`}
                                                </label>
                                            </li>
                                        ))}
                                    </ul>
                                )}
                            </li>
                        ))}
                    </ul>
                )}
                <button onClick={handleConfirm}>Готово</button>
                <button onClick={onClose}>Закрыть</button>
            </div>
        </div>
    );
};

export default EquipmentList;
