import React, { useState, useEffect } from "react";

const Inventory = () => {
    const [inventory, setInventory] = useState([]);
    const [loading, setLoading] = useState(true);
    const [rentedCount, setRentedCount] = useState(0);
    const [sortBy, setSortBy] = useState('name');
    const [newItem, setNewItem] = useState({
        name: "",
        category: "",
        subcategory: "",
        status: "",
        notes: "",
        total: 0
    });

    // Загрузка инвентаря с сортировкой
    useEffect(() => {
        fetch(`/api/inventory?sort_by=${sortBy}`)
            .then((res) => res.json())
            .then((data) => {
                setInventory(data);
                setLoading(false);
            })
            .catch((error) => {
                console.error('Error fetching inventory:', error);
                setLoading(false);
            });
    }, [sortBy]);

    const handleRentedChange = (e, itemId) => {
        const updatedInventory = inventory.map(item => 
            item.id === itemId ? { ...item, rented: e.target.value } : item
        );
        setInventory(updatedInventory);
        setRentedCount(e.target.value);
    };

    const handleSave = (itemId) => {
        fetch(`/api/inventory/${itemId}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ rented: rentedCount }),
        })
        .then((res) => res.json())
        .then(() => {
            alert("Успешно обновлено");
        });
    };

    const handleCreate = () => {
        fetch('/api/inventory/create', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(newItem),
        })
        .then(response => response.json())
        .then(data => {
            if (data.success) {
                alert("Оборудование добавлено");
                setInventory(prev => [...prev, { ...newItem, id: data.id }]);
            }
        })
        .catch(error => console.error("Ошибка создания оборудования:", error));
    };

    return (
        <div>
            <h2>Инвентарь</h2>
            {loading ? (
                <p>Загрузка...</p>
            ) : (
                <>
                    <div>
                        <button onClick={() => setSortBy('name')}>Сортировать по имени</button>
                        <button onClick={() => setSortBy('category')}>Сортировать по категории</button>
                    </div>
                    <table>
                        <thead>
                            <tr>
                                <th>Название</th>
                                <th>Категория</th>
                                <th>Арендовано</th>
                                <th>Обновить</th>
                            </tr>
                        </thead>
                        <tbody>
                            {inventory.map(item => (
                                <tr key={item.id}>
                                    <td>{item.name}</td>
                                    <td>{item.category}</td>
                                    <td>
                                        <input 
                                            type="number" 
                                            value={item.rented}
                                            onChange={(e) => handleRentedChange(e, item.id)} 
                                        />
                                    </td>
                                    <td>
                                        <button onClick={() => handleSave(item.id)}>Сохранить</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>

                    <h3>Добавить новое оборудование</h3>
                    <input
                        type="text"
                        placeholder="Название"
                        value={newItem.name}
                        onChange={e => setNewItem({ ...newItem, name: e.target.value })}
                    />
                    <input
                        type="text"
                        placeholder="Категория"
                        value={newItem.category}
                        onChange={e => setNewItem({ ...newItem, category: e.target.value })}
                    />
                    <input
                        type="text"
                        placeholder="Подкатегория"
                        value={newItem.subcategory}
                        onChange={e => setNewItem({ ...newItem, subcategory: e.target.value })}
                    />
                    <input
                        type="text"
                        placeholder="Статус"
                        value={newItem.status}
                        onChange={e => setNewItem({ ...newItem, status: e.target.value })}
                    />
                    <input
                        type="number"
                        placeholder="Общее количество"
                        value={newItem.total}
                        onChange={e => setNewItem({ ...newItem, total: e.target.value })}
                    />
                    <textarea
                        placeholder="Примечания"
                        value={newItem.notes}
                        onChange={e => setNewItem({ ...newItem, notes: e.target.value })}
                    ></textarea>
                    <button onClick={handleCreate}>Добавить оборудование</button>
                </>
            )}
        </div>
    );
};

export default Inventory;
