import React, { useState, useEffect } from "react";
import EquipmentList from "./EquipmentList";
import StatusSelect from "./StatusSelect";
import "./RentalRow.css";

const RentalRow = ({ booking, onUpdate, onDelete }) => {
    const [rental, setRental] = useState({
        ...booking,
        equipment: Array.isArray(booking.equipment) ? booking.equipment : []
    });

    const [showEquipmentList, setShowEquipmentList] = useState(false);
    const [conflicts, setConflicts] = useState([]);

    useEffect(() => {
        console.log("üì¢ –û–±–Ω–æ–≤–ª–µ–Ω–∏–µ rental:", booking);
        setRental({
            ...booking,
            equipment: Array.isArray(booking.equipment) ? booking.equipment : []
        });
    }, [booking]);

    useEffect(() => {
        checkConflicts();
    }, [rental.start_date, rental.end_date, rental.status]);

    // –ü—Ä–æ–≤–µ—Ä–∫–∞ –∫–æ–Ω—Ñ–ª–∏–∫—Ç–æ–≤ —Å –¥–∞—Ç–∞–º–∏ –∏ —Å—Ç–∞—Ç—É—Å–æ–º
    const checkConflicts = () => {
        const newConflicts = [];
        const now = new Date(new Date().toLocaleString("en-US", { timeZone: "Asia/Almaty" }));

        if (!rental.start_date || !rental.end_date) {
            newConflicts.push({ message: "‚ùå –£–∫–∞–∂–∏—Ç–µ –¥–∞—Ç—É –∏ –≤—Ä–µ–º—è", severity: "error" });
        } else {
            const startDateTime = new Date(rental.start_date);
            const endDateTime = new Date(rental.end_date);

            if (startDateTime >= endDateTime) {
                newConflicts.push({ message: "‚ö†Ô∏è –î–∞—Ç–∞/–≤—Ä–µ–º—è –Ω–∞—á–∞–ª–∞ –ø–æ–∑–∂–µ –∫–æ–Ω—Ü–∞!", severity: "error" });
            }

            if (startDateTime <= now && rental.status === "–ë—Ä–æ–Ω—å") {
                newConflicts.push({
                    message: "‚ö†Ô∏è –î–∞—Ç–∞ –Ω–∞—á–∞–ª–∞ –Ω–∞—Å—Ç—É–ø–∏–ª–∞, –Ω–æ —Å—Ç–∞—Ç—É—Å –Ω–µ '–í—ã–¥–∞–Ω–æ'",
                    severity: "warning"
                });
            }

            if (endDateTime <= now && rental.status !== "–í–æ–∑–≤—Ä–∞—â–µ–Ω–æ") {
                newConflicts.push({
                    message: "‚ö†Ô∏è –î–∞—Ç–∞ –æ–∫–æ–Ω—á–∞–Ω–∏—è –ø—Ä–æ—à–ª–∞, –Ω–æ —Å—Ç–∞—Ç—É—Å –Ω–µ '–í–æ–∑–≤—Ä–∞—â–µ–Ω–æ'",
                    severity: "warning"
                });
            }
        }

        setConflicts(newConflicts);
    };

    // –û–±–Ω–æ–≤–ª–µ–Ω–∏–µ –¥–∞–Ω–Ω—ã—Ö
    const handleChange = (e) => {
        const { name, value } = e.target;
        setRental(prev => ({ ...prev, [name]: value }));
    };

    // –°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ –∏–∑–º–µ–Ω–µ–Ω–∏–π –ø—Ä–∏ –ø–æ—Ç–µ—Ä–µ —Ñ–æ–∫—É—Å–∞
    const handleBlur = () => {
        onUpdate(rental.id, rental);
    };

    return (
        <tr>
            {/* –î–∞—Ç–∞ –∞—Ä–µ–Ω–¥—ã */}
            <td>
                <input
                    type="datetime-local"
                    name="start_date"
                    value={rental.start_date ? new Date(rental.start_date).toISOString().slice(0, 16) : ""}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    step="900"
                />
                <input
                    type="datetime-local"
                    name="end_date"
                    value={rental.end_date ? new Date(rental.end_date).toISOString().slice(0, 16) : ""}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    step="900"
                />
            </td>

            {/* –ü–æ–ª—è —Å –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–µ–π */}
            <td><input type="text" name="renter" value={rental.renter || ""} onChange={handleChange} onBlur={handleBlur} /></td>
            <td><input type="text" name="issuer" value={rental.issuer || ""} onChange={handleChange} onBlur={handleBlur} /></td>
            <td><input type="text" name="receiver" value={rental.receiver || ""} onChange={handleChange} onBlur={handleBlur} /></td>

            {/* –í—ã–±–æ—Ä –æ–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏—è */}
            <td>
    <div className="equipment-container">
        {/* –õ–æ–≥–∏—Ä—É–µ–º —Ç–µ–∫—É—â–∏–µ –¥–∞–Ω–Ω—ã–µ */}
        {console.log("üì¶ –û—Ç–æ–±—Ä–∞–∂–∞–µ–º–æ–µ –æ–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏–µ:", rental.equipment)}

        {/* –ï—Å–ª–∏ –µ—Å—Ç—å –æ–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏–µ, —Ä–µ–Ω–¥–µ—Ä–∏–º */}
        {rental.equipment?.length > 0 && rental.equipment.map((eq, index) => (
            <span key={index} className="equipment-tag">
                {eq.name} √ó {eq.quantity || 1}
            </span>
        ))}

        {/* –ö–Ω–æ–ø–∫–∞ –≤—ã–±–æ—Ä–∞ –≤—Å–µ–≥–¥–∞ –¥–æ—Å—Ç—É–ø–Ω–∞ */}
        <button onClick={() => setShowEquipmentList(true)}>–í—ã–±—Ä–∞—Ç—å –æ–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏–µ</button>
    </div>

    {/* –û–∫–Ω–æ –≤—ã–±–æ—Ä–∞ –æ–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏—è */}
    {showEquipmentList && (
        <EquipmentList
            selectedEquipment={rental.equipment}
            onSelect={(selectedEquipment) => {
                console.log("‚úÖ –í—ã–±—Ä–∞–Ω–æ –æ–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏–µ:", selectedEquipment);
                const updatedRental = { ...rental, equipment: selectedEquipment };
                setRental(updatedRental);
                onUpdate(rental.id, updatedRental);
                setShowEquipmentList(false);
            }}
            onClose={() => setShowEquipmentList(false)}
        />
    )}
</td>


            
            {/* –ü—Ä–∏–º–µ—á–∞–Ω–∏—è */}
            <td><input type="text" name="notes" value={rental.notes || ""} onChange={handleChange} onBlur={handleBlur} /></td>

            {/* –°—Ç–∞—Ç—É—Å */}
            <td>
                <StatusSelect
                    value={rental.status}
                    onChange={(status) => {
                        const updatedRental = { ...rental, status };
                        setRental(updatedRental);
                        onUpdate(rental.id, updatedRental);
                    }}
                />
            </td>

            {/* –ö–æ–Ω—Ñ–ª–∏–∫—Ç—ã */}
            <td>
                {conflicts.length > 0 ? (
                    <div>
                        {conflicts.map((conflict, index) => (
                            <div key={index} className={`conflict-${conflict.severity}`}>
                                {conflict.message}
                            </div>
                        ))}
                    </div>
                ) : "‚úì"}
            </td>

            {/* –£–¥–∞–ª–µ–Ω–∏–µ */}
            <td>
                <button onClick={() => onDelete(rental.id)}>‚úï</button>
            </td>
        </tr>
    );
};

export default RentalRow;
