import React, { useState, useEffect, useCallback } from "react";
import DatePicker from "react-datepicker";
import StatusSelect from "./StatusSelect";
import EquipmentSelect from "./EquipmentSelect";
import "react-datepicker/dist/react-datepicker.css";
import "./styles.css";

function Table() {
  const [rentals, setRentals] = useState([]);
  const [equipmentList, setEquipmentList] = useState([]);
  const [selectedRow, setSelectedRow] = useState(null);

  // Загружаем оборудование при загрузке компонента
  useEffect(() => {
    fetch("/api/inventory")
      .then((res) => res.json())
      .then(setEquipmentList)
      .catch((err) => console.error("Ошибка загрузки оборудования:", err));
  }, []);

  // Функция для обновления аренды (избегаем дублирования кода)
  const updateRental = useCallback((rowId, field, value) => {
    setRentals((prev) =>
      prev.map((rental, index) =>
        index === rowId ? { ...rental, [field]: value } : rental
      )
    );
  }, []);

  // Добавить новую строку аренды
  const addRental = useCallback(() => {
    setRentals((prev) => [
      ...prev,
      {
        id: prev.length + 1,
        startDate: null,
        endDate: null,
        renter: "",
        equipment: [],
        issuer: "",
        receiver: "",
        status: "Бронь",
        notes: "",
      },
    ]);
  }, []);

  return (
    <div className="table-container">
      <button onClick={addRental}>+ Добавить аренду</button>
      <table>
        <thead>
          <tr>
            <th>Дата аренды</th>
            <th>Кто берет</th>
            <th>Оборудование</th>
            <th>Кто выдал</th>
            <th>Кто принял</th>
            <th>Статус</th>
            <th>Примечания</th>
          </tr>
        </thead>
        <tbody>
          {rentals.map((rental, index) => (
            <tr key={index}>
              <td className="date-cell">
                <div className="date-block">
                  <span>С</span>
                  <DatePicker
                    selected={rental.startDate}
                    onChange={(date) => updateRental(index, "startDate", date)}
                    showTimeSelect
                    dateFormat="dd/MM/yy HH:mm"
                    timeFormat="HH:mm"
                    timeIntervals={15}
                    placeholderText="Выбрать"
                    className="date-picker"
                  />
                </div>
                <div className="date-block">
                  <span>По</span>
                  <DatePicker
                    selected={rental.endDate}
                    onChange={(date) => updateRental(index, "endDate", date)}
                    showTimeSelect
                    dateFormat="dd/MM/yy HH:mm"
                    timeFormat="HH:mm"
                    timeIntervals={15}
                    placeholderText="Выбрать"
                    className="date-picker"
                  />
                </div>
              </td>

              <td>
                <input
                  type="text"
                  value={rental.renter}
                  onChange={(e) => updateRental(index, "renter", e.target.value)}
                />
              </td>

              <td onClick={() => setSelectedRow(index)}>
                {rental.equipment.length > 0
                  ? rental.equipment.join(", ")
                  : "Выбрать"}
              </td>

              <td>
                <input
                  type="text"
                  value={rental.issuer}
                  onChange={(e) => updateRental(index, "issuer", e.target.value)}
                />
              </td>

              <td>
                <input
                  type="text"
                  value={rental.receiver}
                  onChange={(e) => updateRental(index, "receiver", e.target.value)}
                />
              </td>

              <td>
                <StatusSelect
                  status={rental.status}
                  onChange={(status) => updateRental(index, "status", status)}
                />
              </td>

              <td>
                <input
                  type="text"
                  value={rental.notes}
                  onChange={(e) => updateRental(index, "notes", e.target.value)}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {selectedRow !== null && (
        <EquipmentSelect
          onSelect={(selected) => updateRental(selectedRow, "equipment", selected)}
          onClose={() => setSelectedRow(null)}
        />
      )}
    </div>
  );
}

export default Table;
