import React, { useState } from "react";
import "./styles.css";
import Calendar from "./Calendar";
import EquipmentList from "./EquipmentList";

const Sidebar = ({ toggleShift }) => {
    const [isOpen, setIsOpen] = useState(false);
    const [activeTab, setActiveTab] = useState(null); // Управляем отображением содержимого

    const handleToggle = () => {
        setIsOpen(!isOpen);
        toggleShift(!isOpen);
    };

    return (
        <div className={`sidebar ${isOpen ? "open" : ""}`}>
            <button 
                className={`sidebar-toggle ${isOpen ? "rotated" : ""}`} 
                onClick={handleToggle}
            >
                &gt;
            </button>

            <div className="sidebar-buttons">
                <button onClick={() => setActiveTab("date")}>Дата аренды</button>
                <button onClick={() => setActiveTab("inventory")}>Инвентарь</button>
            </div>

            <h3>Инвентарь</h3>

            <div className="sidebar-content">
                {activeTab === "date" && <Calendar />}
                {activeTab === "inventory" && <EquipmentList />}
            </div>
        </div>
    );
};

export default Sidebar;
