import React, { useState, useEffect } from "react";
import DatePicker from "react-datepicker";
import StatusSelect from "./StatusSelect";
import EquipmentSelect from "./EquipmentSelect";
import "react-datepicker/dist/react-datepicker.css";
import "./styles.css";

function Table() {
  const [rentals, setRentals] = useState([]);
  const [equipmentList, setEquipmentList] = useState([]);
  const [selectedRow, setSelectedRow] = useState(null); // Выбранная строка для редактирования оборудования

  useEffect(() => {
    console.time("Загрузка оборудования"); // Начало замера времени загрузки
    fetch("/api/inventory") // Загружаем список оборудования
      .then((res) => res.json())
      .then((data) => {
        console.timeEnd("Загрузка оборудования"); // Конец замера времени
        setEquipmentList(data);
      })
      .catch((err) => console.error("Ошибка загрузки оборудования:", err));
  }, []);

  const addRental = () => {
    setRentals([
      ...rentals,
      {
        id: rentals.length + 1,
        startDate: null,
        endDate: null,
        renter: "",
        equipment: [],
        issuer: "",
        receiver: "",
        status: "Бронь",
        notes: "",
        conflicts: "", // Столбец для ошибок
      },
    ]);
  };

  const handleDateChange = (rowId, type, date) => {
    let newRentals = [...rentals];
    newRentals[rowId][type] = date;

    // Проверка на ошибки
    let conflictMessage = "";
    if (!newRentals[rowId].startDate || !newRentals[rowId].endDate) {
      conflictMessage = "Необходимо указать обе даты";
    } else if (newRentals[rowId].startDate > newRentals[rowId].endDate) {
      conflictMessage = "Дата окончания не может быть раньше даты начала";
    }

    newRentals[rowId].conflicts = conflictMessage;
    setRentals(newRentals);
  };

  return (
    <div className="table-container">
      <button onClick={addRental}>+ Добавить аренду</button>
      <table>
        <thead>
          <tr>
            <th>Дата аренды</th>
            <th>Кто берет</th>
            <th>Оборудование</th>
            <th>Кто выдал</th>
            <th>Кто принял</th>
            <th>Статус</th>
            <th>Примечания</th>
            <th>Конфликты</th> {/* Новый столбец */}
          </tr>
        </thead>
        <tbody>
          {rentals.map((rental, index) => (
            <tr key={index}>
              <td className="date-cell">
                <div className="date-block">
                  <span>С</span>
                  <DatePicker
                    selected={rental.startDate}
                    onChange={(date) => handleDateChange(index, "startDate", date)}
                    showTimeSelect
                    dateFormat="dd/MM/yy HH:mm"
                    timeFormat="HH:mm"
                    timeIntervals={15}
                    placeholderText="Выбрать"
                    className="date-picker"
                  />
                </div>
                <div className="date-block">
                  <span>По</span>
                  <DatePicker
                    selected={rental.endDate}
                    onChange={(date) => handleDateChange(index, "endDate", date)}
                    showTimeSelect
                    dateFormat="dd/MM/yy HH:mm"
                    timeFormat="HH:mm"
                    timeIntervals={15}
                    placeholderText="Выбрать"
                    className="date-picker"
                  />
                </div>
              </td>
              <td>
                <input
                  type="text"
                  value={rental.renter}
                  onChange={(e) => {
                    let newRentals = [...rentals];
                    newRentals[index].renter = e.target.value;
                    setRentals(newRentals);
                  }}
                />
              </td>
              <td onClick={() => setSelectedRow(index)}>
                {rental.equipment.length > 0 ? rental.equipment.join(", ") : "Выбрать"}
              </td>
              <td>
                <input
                  type="text"
                  value={rental.issuer}
                  onChange={(e) => {
                    let newRentals = [...rentals];
                    newRentals[index].issuer = e.target.value;
                    setRentals(newRentals);
                  }}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={rental.receiver}
                  onChange={(e) => {
                    let newRentals = [...rentals];
                    newRentals[index].receiver = e.target.value;
                    setRentals(newRentals);
                  }}
                />
              </td>
              <td>
                <StatusSelect
                  status={rental.status}
                  onChange={(status) => {
                    let newRentals = [...rentals];
                    newRentals[index].status = status;
                    setRentals(newRentals);
                  }}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={rental.notes}
                  onChange={(e) => {
                    let newRentals = [...rentals];
                    newRentals[index].notes = e.target.value;
                    setRentals(newRentals);
                  }}
                />
              </td>
              <td className="conflict-cell">
                {rental.conflicts && <span className="conflict-message">{rental.conflicts}</span>}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {selectedRow !== null && (
        <EquipmentSelect
          onSelect={(selected) => {
            let newRentals = [...rentals];
            newRentals[selectedRow].equipment = selected;
            setRentals(newRentals);
            setSelectedRow(null);
          }}
          onClose={() => setSelectedRow(null)}
        />
      )}
    </div>
  );
}

export default Table;
