import React, { useState, useEffect } from "react";
import EquipmentList from "./EquipmentList";
import StatusSelect from "./StatusSelect";
import './RentalRow.css';

const RentalRow = ({ booking, onUpdate, onDelete, isNew }) => {
    const [rental, setRental] = useState({
        ...booking,
        equipment: Array.isArray(booking.equipment) ? booking.equipment : []
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setRental(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleBlur = () => {
        const updatedRental = { ...rental, equipment: JSON.stringify(rental.equipment) };
        onUpdate(rental.id, updatedRental);
    };

    return (
        <tr className={isNew ? "new-rental" : ""}>
            {/* Дата аренды */}
            <td>
                <input
                    type="datetime-local"
                    name="start_date"
                    value={rental.start_date || ""}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    className="rental-date-input"
                />
                <input
                    type="datetime-local"
                    name="end_date"
                    value={rental.end_date || ""}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    className="rental-date-input"
                />
            </td>

            {/* Имя арендатора */}
            <td>
                <input
                    type="text"
                    name="renter"
                    value={rental.renter || ""}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    placeholder="Имя арендатора"
                    className="rental-input"
                />
            </td>

            {/* Кто выдал */}
            <td>
                <input
                    type="text"
                    name="issuer"
                    value={rental.issuer || ""}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    placeholder="Кто выдал"
                    className="rental-input"
                />
            </td>

            {/* Кто принял */}
            <td>
                <input
                    type="text"
                    name="receiver"
                    value={rental.receiver || ""}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    placeholder="Кто принял"
                    className="rental-input"
                />
            </td>

            {/* Оборудование */}
            <td>
                <button className="equipment-button">Выбрать оборудование</button>
            </td>

            {/* Примечания */}
            <td>
                <input
                    type="text"
                    name="notes"
                    value={rental.notes || ""}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    placeholder="Примечания"
                    className="rental-input"
                />
            </td>

            {/* Статус */}
            <td>
                <StatusSelect
                    value={rental.status}
                    onChange={(status) => {
                        setRental(prev => {
                            const updatedRental = { ...prev, status };
                            onUpdate(prev.id, updatedRental);
                            return updatedRental;
                        });
                    }}
                />
            </td>

            {/* Удаление */}
            <td>
                <button onClick={() => onDelete(rental.id)} className="delete-button">
                    ✕
                </button>
            </td>
        </tr>
    );
};

export default RentalRow;
