import React, { useState, useEffect, useRef } from "react";
import ReactDOM from 'react-dom';
import API_BASE_URL from "../config";
import './EquipmentList.css';

const EquipmentList = ({ onSelect, onClose, selectedEquipment }) => {
    const listRef = useRef(null);
    const [equipment, setEquipment] = useState([]);
    const [loading, setLoading] = useState(true);
    const [selectedItems, setSelectedItems] = useState(Array.isArray(selectedEquipment) ? selectedEquipment : []);
    const [searchTerm, setSearchTerm] = useState("");
    const modalRoot = useRef(document.createElement('div'));

    useEffect(() => {
        modalRoot.current.id = 'modal-root';
        document.body.appendChild(modalRoot.current);

        return () => {
            document.body.removeChild(modalRoot.current);
        };
    }, []);
    
    useEffect(() => {
        const fetchEquipment = async () => {
            try {
                const response = await fetch(`${API_BASE_URL}/inventory`);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                console.log("Пример элемента:", data[0]); // Посмотрим структуру первого элемента
                console.log("Количество элементов:", data.length);
                
                setEquipment(data);
                setLoading(false);
            } catch (error) {
                console.error("Ошибка загрузки оборудования:", error);
                setLoading(false);
            }
        };
    
        fetchEquipment();

        const handleClickOutside = (event) => {
            if (listRef.current && !listRef.current.contains(event.target)) {
                onClose();
            }
        };

        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, [onClose]);

    const toggleSelection = (item) => {
        setSelectedItems((prevSelected) => {
            if (!Array.isArray(prevSelected)) prevSelected = [];
            const existingItem = prevSelected.find((selected) => selected.id === item.id);
            if (existingItem) {
                return prevSelected.filter((selected) => selected.id !== item.id);
            } else {
                const quantity = Math.min(1, item.fact); // Используем fact для доступного количества
                return [...prevSelected, { ...item, quantity }];
            }
        });
    };

    const updateQuantity = (itemId, newQuantity, maxAvailable) => {
        if (!Array.isArray(selectedItems)) return;
        const item = equipment.find(eq => eq.id === itemId);
        if (!item) return;

        const quantity = Math.min(Math.max(1, newQuantity), item.fact); // Используем fact для доступного количества
        
        setSelectedItems(selectedItems.map((item) =>
            item.id === itemId ? { ...item, quantity } : item
        ));
    };

    const handleConfirm = () => {
        onSelect(Array.isArray(selectedItems) ? selectedItems : []);
        onClose();
    };

    const filteredEquipment = equipment.filter((item) =>
        item.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return ReactDOM.createPortal(
        <div className="equipment-modal" ref={listRef}>
            <div className="equipment-modal-content">
                <h3>Выберите оборудование</h3>
                <input
                    type="text"
                    placeholder="Поиск..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="equipment-search"
                />
                {loading ? (
                    <p>Загрузка...</p>
                ) : (
                    <ul className="equipment-list">
                        {filteredEquipment.map((item) => {
                            const selectedItem = selectedItems.find(
                                selected => selected.id === item.id
                            );
                            const isAvailable = item.fact > 0; // Используем fact для проверки доступности

                            return (
                                <li key={item.id} className="equipment-item">
                                    <div className="equipment-controls">
                                        {selectedItem && (
                                            <div className="quantity-control">
                                                <button 
                                                    onClick={(e) => {
                                                        e.stopPropagation();
                                                        updateQuantity(
                                                            item.id, 
                                                            selectedItem.quantity - 1,
                                                            item.fact
                                                        );
                                                    }}
                                                    className="quantity-button"
                                                    disabled={selectedItem.quantity <= 1}
                                                >
                                                    -
                                                </button>
                                                <input
                                                    type="number"
                                                    value={selectedItem.quantity}
                                                    onChange={(e) => updateQuantity(
                                                        item.id,
                                                        parseInt(e.target.value) || 1,
                                                        item.fact
                                                    )}
                                                    className="quantity-input"
                                                    min="1"
                                                    max={item.fact}
                                                />
                                                <button 
                                                    onClick={(e) => {
                                                        e.stopPropagation();
                                                        updateQuantity(
                                                            item.id,
                                                            selectedItem.quantity + 1,
                                                            item.fact
                                                        );
                                                    }}
                                                    className="quantity-button"
                                                    disabled={selectedItem.quantity >= item.fact}
                                                >
                                                    +
                                                </button>
                                            </div>
                                        )}
                                        <label className="equipment-label">
                                            <input
                                                type="checkbox"
                                                checked={!!selectedItem}
                                                onChange={() => toggleSelection(item)}
                                                disabled={!isAvailable && !selectedItem}
                                            />
                                            <span>{item.name}</span>
                                        </label>
                                    </div>
                                    <div className={`availability ${isAvailable ? 'available' : 'unavailable'}`}>
                                        {isAvailable 
                                            ? `Доступно: ${item.fact}` 
                                            : "Нет в наличии"
                                        }
                                    </div>
                                </li>
                            );
                        })}
                    </ul>
                )}
                <div className="modal-buttons">
                    <button onClick={onClose} className="button button-cancel">
                        Отмена
                    </button>
                    <button onClick={handleConfirm} className="button button-confirm">
                        Готово
                    </button>
                </div>
            </div>
        </div>,
        modalRoot.current
    );
};

export default EquipmentList;
