import React from "react";
import ConflictsColumn from "./ConflictsColumn";
import EquipmentList from "./EquipmentList";

function RentalRow({ rental, updateRental }) {
  const handleDateChange = (field, value) => {
    updateRental(rental.id, { ...rental, [field]: value });
  };

  const handleEquipmentSelection = (selectedEquipment) => {
    updateRental(rental.id, { ...rental, equipment: selectedEquipment });
  };

  return (
    <tr>
      <td>
        <EquipmentList onSelect={handleEquipmentSelection} />
        {rental.equipment.length > 0 ? rental.equipment.join(", ") : "Выбрать оборудование"}
      </td>
      <td>
        <input
          type="date"
          value={rental.startDate}
          onChange={(e) => handleDateChange("startDate", e.target.value)}
        />
      </td>
      <td>
        <input
          type="date"
          value={rental.endDate}
          onChange={(e) => handleDateChange("endDate", e.target.value)}
        />
      </td>
      <td>{rental.status}</td>
      <td>
        <ConflictsColumn startDate={rental.startDate} endDate={rental.endDate} />
      </td>
    </tr>
  );
}

export default RentalRow;
