import React, { useState } from "react";
import Calendar from "./Calendar";
import StatusSelect from "./StatusSelect";
import "./styles.css";

function Table() {
  const [rentals, setRentals] = useState([]);
  const [selectedRow, setSelectedRow] = useState(null);

  const addRental = () => {
    setRentals([
      ...rentals,
      {
        id: rentals.length + 1,
        date: "",
        renter: "",
        equipment: "",
        issuer: "",
        receiver: "",
        status: "Бронь",
        notes: "",
      },
    ]);
  };

  const handleDateSelect = (rowId, date) => {
    if (!date) return; // Защита от undefined

    let newRentals = [...rentals];
    newRentals[rowId].date = date.toLocaleDateString("ru-RU"); // Безопасное преобразование
    setRentals(newRentals);
    setSelectedRow(null); // Закрываем календарь после выбора
  };

  return (
    <div className="table-container">
      <button onClick={addRental}>+ Добавить аренду</button>
      <table>
        <thead>
          <tr>
            <th>Дата аренды</th>
            <th>Кто берет</th>
            <th>Оборудование</th>
            <th>Кто выдал</th>
            <th>Кто принял</th>
            <th>Статус</th>
            <th>Примечания</th>
          </tr>
        </thead>
        <tbody>
          {rentals.map((rental, index) => (
            <tr key={index}>
              <td
                className="date-cell"
                onClick={() => setSelectedRow(index)}
              >
                {rental.date || "Выбрать дату"}
              </td>
              <td>
                <input
                  type="text"
                  value={rental.renter}
                  onChange={(e) => {
                    let newRentals = [...rentals];
                    newRentals[index].renter = e.target.value;
                    setRentals(newRentals);
                  }}
                />
              </td>
              <td onClick={() => alert("Открыть список оборудования")}>
                {rental.equipment || "Выбрать"}
              </td>
              <td>
                <input
                  type="text"
                  value={rental.issuer}
                  onChange={(e) => {
                    let newRentals = [...rentals];
                    newRentals[index].issuer = e.target.value;
                    setRentals(newRentals);
                  }}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={rental.receiver}
                  onChange={(e) => {
                    let newRentals = [...rentals];
                    newRentals[index].receiver = e.target.value;
                    setRentals(newRentals);
                  }}
                />
              </td>
              <td>
                <StatusSelect
                  status={rental.status}
                  onChange={(status) => {
                    let newRentals = [...rentals];
                    newRentals[index].status = status;
                    setRentals(newRentals);
                  }}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={rental.notes}
                  onChange={(e) => {
                    let newRentals = [...rentals];
                    newRentals[index].notes = e.target.value;
                    setRentals(newRentals);
                  }}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {selectedRow !== null && (
        <div className="calendar-popup">
          <Calendar
            onClose={() => setSelectedRow(null)}
            onDateSelect={(date) => handleDateSelect(selectedRow, date)}
          />
        </div>
      )}
    </div>
  );
}

export default Table;
