import React, { useState, useEffect } from "react";
import RentalRow from "./RentalRow";
import API_BASE_URL from "../config";
import './Table.css';

const Table = () => {
    const [bookings, setBookings] = useState([]);
    const [error, setError] = useState(null);


    const fetchBookings = async () => {
        try {
            const response = await fetch(`${API_BASE_URL}/bookings`);
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const data = await response.json();
            console.log("Fetched bookings:", data);
            setBookings(Array.isArray(data) ? data : []);
        } catch (err) {
            console.error("Error loading data:", err);
            setError(err.message);
        }
    };

    useEffect(() => {
        fetchBookings();
    }, []);

    const addRental = () => {
        const newRental = {
            id: Date.now(),
            start_date: "",
            end_date: "",
            renter: "",
            equipment: [],
            issuer: "",
            receiver: "",
            status: "–ë—Ä–æ–Ω—å",
            notes: ""
        };

        setBookings(prevBookings => [...prevBookings, newRental]);
    };

    const saveRental = async (id, rentalData) => {
        try {
            console.log("Sending rental data:", rentalData);

            const formattedRental = {
                ...rentalData,
                start_date: rentalData.start_date ? rentalData.start_date.replace('T', ' ') : null,
                end_date: rentalData.end_date ? rentalData.end_date.replace('T', ' ') : null,
                equipment: Array.isArray(rentalData.equipment) ? rentalData.equipment : []
            };

            console.log("Formatted rental data:", formattedRental);

            const response = await fetch(`${API_BASE_URL}/bookings`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formattedRental)
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.error || `HTTP error! status: ${response.status}`);
            }

            const savedRental = await response.json();
            await fetchBookings();  // –û–±–Ω–æ–≤–ª—è–µ–º —Å–ø–∏—Å–æ–∫ –≤—Å–µ—Ö –∑–∞–ø–∏—Å–µ–π
            return true;
        } catch (error) {
            console.error("–û—à–∏–±–∫–∞ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è:", error);
            setError(error.message || "–ù–µ —É–¥–∞–ª–æ—Å—å —Å–æ—Ö—Ä–∞–Ω–∏—Ç—å –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ");
            return false;
        }
    };

    const updateRental = async (id, updatedRental) => {
        try {
            if (id.toString().length === 13) { // –ü—Ä–æ–≤–µ—Ä–∫–∞ –Ω–∞ –≤—Ä–µ–º–µ–Ω–Ω—ã–π ID
                setBookings(prevBookings => 
                    prevBookings.map(booking => 
                        booking.id === id ? updatedRental : booking
                    )
                );
                return;
            }

            const formattedRental = {
                ...updatedRental,
                start_date: updatedRental.start_date.replace('T', ' '),
                end_date: updatedRental.end_date.replace('T', ' '),
                equipment: Array.isArray(updatedRental.equipment) ? updatedRental.equipment : []
            };

            const response = await fetch(`${API_BASE_URL}/bookings/${id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formattedRental)
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || `HTTP error! status: ${response.status}`);
            }

            await fetchBookings();  // –û–±–Ω–æ–≤–ª—è–µ–º —Å–ø–∏—Å–æ–∫ –≤—Å–µ—Ö –∑–∞–ø–∏—Å–µ–π
        } catch (error) {
            console.error("–û—à–∏–±–∫–∞ –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è:", error);
            setError(error.message || "–ù–µ —É–¥–∞–ª–æ—Å—å –æ–±–Ω–æ–≤–∏—Ç—å –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ");
        }
    };

    const handleDelete = async (id) => {
        if (!id) {
            console.error("ID –Ω–µ —É–∫–∞–∑–∞–Ω –¥–ª—è —É–¥–∞–ª–µ–Ω–∏—è");
            return;
        }

        try {
            const response = await fetch(`${API_BASE_URL}/bookings/${id}`, {
                method: 'DELETE',
                headers: {
                    'Content-Type': 'application/json',
                }
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || `HTTP error! status: ${response.status}`);
            }

            await fetchBookings();  // –û–±–Ω–æ–≤–ª—è–µ–º —Å–ø–∏—Å–æ–∫ –≤—Å–µ—Ö –∑–∞–ø–∏—Å–µ–π
        } catch (error) {
            console.error("–û—à–∏–±–∫–∞ —É–¥–∞–ª–µ–Ω–∏—è:", error);
            setError(error.message || "–ù–µ —É–¥–∞–ª–æ—Å—å —É–¥–∞–ª–∏—Ç—å –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ");
        }
    };

    return (
        <div>
            <h2>üìã –¢–∞–±–ª–∏—Ü–∞ –∞—Ä–µ–Ω–¥—ã</h2>
            {error && <p className="error-message">{error}</p>}
            <button onClick={addRental} className="add-button">‚ûï –î–æ–±–∞–≤–∏—Ç—å –∞—Ä–µ–Ω–¥—É</button>
            <table className="rental-table">
                <thead>
                    {/* ... –∑–∞–≥–æ–ª–æ–≤–∫–∏ –æ—Å—Ç–∞—é—Ç—Å—è –±–µ–∑ –∏–∑–º–µ–Ω–µ–Ω–∏–π ... */}
                </thead>
                <tbody>
                    {bookings.map((booking) => (
                        <RentalRow 
                            key={booking.id}
                            booking={booking}
                            onUpdate={updateRental}
                            onDelete={() => handleDelete(booking.id)}
                            // –£–±—Ä–∞–Ω onSave –∏–∑ –ø—Ä–æ–ø—Å–æ–≤
                        />
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Table;
