import React, { useEffect, useState, useRef } from "react";
import API_BASE_URL from "../config"; // Импортируем базовый URL

const EquipmentList = ({ onSelect, onClose }) => {
    const [equipment, setEquipment] = useState([]);
    const [loading, setLoading] = useState(true);
    const [selectedItems, setSelectedItems] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const listRef = useRef(null);

    useEffect(() => {
        fetch(`${API_BASE_URL}/inventory`)
            .then((res) => res.json())
            .then((data) => {
                setEquipment(data);
                setLoading(false);
            })
            .catch((error) => {
                console.error("Ошибка загрузки оборудования:", error);
                setLoading(false);
            });

        const handleClickOutside = (event) => {
            if (listRef.current && !listRef.current.contains(event.target)) {
                onClose();
            }
        };

        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, [onClose]);

    const toggleSelection = (item) => {
        setSelectedItems((prevSelected) => {
            const existingItem = prevSelected.find((selected) => selected.id === item.id);
            if (existingItem) {
                return prevSelected.filter((selected) => selected.id !== item.id);
            } else {
                return [...prevSelected, { ...item, quantity: 1 }];
            }
        });
    };

    const updateQuantity = (itemId, newQuantity) => {
        setSelectedItems((prevSelected) =>
            prevSelected.map((item) =>
                item.id === itemId ? { ...item, quantity: newQuantity } : item
            )
        );
    };

    const handleConfirm = () => {
        onSelect(selectedItems);
        onClose();
    };

    const filteredEquipment = equipment.filter((item) =>
        item.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div className="modal" ref={listRef}>
            <div className="modal-content">
                <h3>Выберите оборудование</h3>
                <input
                    type="text"
                    placeholder="Поиск..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    style={{ width: "100%", marginBottom: "10px" }}
                />
                {loading ? (
                    <p>Загрузка...</p>
                ) : (
                    <ul style={{ maxHeight: "300px", overflowY: "auto" }}>
                        {filteredEquipment.map((item) => {
                            const selectedItem = selectedItems.find((selected) => selected.id === item.id);
                            return (
                                <li key={item.id}>
                                    <label>
                                        <input
                                            type="checkbox"
                                            checked={!!selectedItem}
                                            onChange={() => toggleSelection(item)}
                                        />
                                        {item.name} | {item.available > 0 ? `Доступно: ${item.available}` : "❌ Нет в наличии"}
                                        {item.next_available && ` | Освободится: ${item.next_available}`}
                                    </label>
                                    {selectedItem && (
                                        <div style={{ display: "inline-block", marginLeft: "10px" }}>
                                            <button onClick={() => updateQuantity(item.id, Math.max(1, selectedItem.quantity - 1))}>-</button>
                                            <input
                                                type="number"
                                                value={selectedItem.quantity}
                                                onChange={(e) => updateQuantity(item.id, Math.max(1, parseInt(e.target.value) || 1))}
                                                style={{ width: "40px", textAlign: "center" }}
                                            />
                                            <button onClick={() => updateQuantity(item.id, selectedItem.quantity + 1)}>+</button>
                                        </div>
                                    )}
                                </li>
                            );
                        })}
                    </ul>
                )}
                <button onClick={handleConfirm}>Готово</button>
                <button onClick={onClose}>Закрыть</button>
            </div>
        </div>
    );
};

export default EquipmentList;
