import React, { useState, useEffect } from "react";
import RentalRow from "./RentalRow";
import API_BASE_URL from "../config";
import './Table.css';

const Table = () => {
    const [bookings, setBookings] = useState([]);
    const [inventory, setInventory] = useState([]); 
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchBookings();
        fetchInventory(); 
    }, []);

    const fetchBookings = async () => {
        try {
            const response = await fetch(`${API_BASE_URL}/bookings`);
            if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
            const data = await response.json();

            setBookings(Array.isArray(data) ? 
                data.map(booking => ({
                    id: booking.id, 
                    start_date: booking.start_date, 
                    end_date: booking.end_date, 
                    name: booking.name || "", 
                    issued_by: booking.issued_by || "", 
                    received_by: booking.received_by || "", 
                    notes: booking.notes || "", 
                    status: booking.status || "–ë—Ä–æ–Ω—å",
                    equipment: Array.isArray(booking.equipment) ? booking.equipment : []
                })) 
                : []
            );
        } catch (err) {
            console.error("Error loading bookings:", err);
            setError(err.message);
        }
    };

    const fetchInventory = async () => {
        try {
            const response = await fetch(`${API_BASE_URL}/inventory`); 
            if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
            const data = await response.json();
            setInventory(Array.isArray(data) ? data : []); 
        } catch (err) {
            console.error("Error loading inventory:", err);
            setError(err.message);
        }
    };

    const addNewRental = () => {
        setBookings([...bookings, { 
            id: Date.now(), 
            start_date: "", 
            end_date: "", 
            name: "", 
            issued_by: "", 
            received_by: "", 
            notes: "", 
            status: "–ë—Ä–æ–Ω—å", 
            equipment: [] 
        }]);
    };

    return (
        <div>
            <h2>üìã –¢–∞–±–ª–∏—Ü–∞ –∞—Ä–µ–Ω–¥—ã</h2>
            {error && <p className="error-message">{error}</p>}

            <button onClick={addNewRental} className="add-button">‚ûï –î–æ–±–∞–≤–∏—Ç—å –∞—Ä–µ–Ω–¥—É</button>
            <button onClick={fetchBookings} className="refresh-button">üîÑ –û–±–Ω–æ–≤–∏—Ç—å –¥–∞–Ω–Ω—ã–µ</button>

            <table className="rental-table">
                <thead>
                    <tr>
                        <th>–î–∞—Ç–∞ –∞—Ä–µ–Ω–¥—ã</th>
                        <th>–ò–º—è</th>
                        <th>–ö—Ç–æ –≤—ã–¥–∞–ª</th>
                        <th>–ö—Ç–æ –ø—Ä–∏–Ω—è–ª</th>
                        <th>–û–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏–µ</th>
                        <th>–ü—Ä–∏–º–µ—á–∞–Ω–∏—è</th>
                        <th>–°—Ç–∞—Ç—É—Å</th>
                        <th>–ö–æ–Ω—Ñ–ª–∏–∫—Ç—ã</th>
                        <th>–î–µ–π—Å—Ç–≤–∏—è</th>
                    </tr>
                </thead>
                <tbody>
                    {bookings.map((booking) => (
                        <RentalRow 
                            key={booking.id}
                            booking={booking}
                            inventory={inventory} 
                        />
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Table;
