import React, { useState, useEffect } from "react";
import EquipmentList from "./EquipmentList";
import StatusSelect from "./StatusSelect";
import "./RentalRow.css";

const RentalRow = ({ booking, inventory, bookings, onUpdate, onDelete }) => {
    const [rental, setRental] = useState({
        id: booking.id || null,
        start_date: booking.start_date || "",
        end_date: booking.end_date || "",
        status: booking.status || "Бронь",
        equipment: Array.isArray(booking.equipment) ? booking.equipment : [],
        name: booking.name || "",
        issued_by: booking.issued_by || "",
        received_by: booking.received_by || "",
        notes: booking.notes || ""
    });

    const [showEquipmentList, setShowEquipmentList] = useState(false);
    const [conflicts, setConflicts] = useState([]);
    const [isEditing, setIsEditing] = useState(false);

    useEffect(() => {
        let parsedEquipment = [];

        if (typeof booking.equipment === "string") {
            try {
                parsedEquipment = JSON.parse(booking.equipment);
            } catch (error) {
                console.error("Ошибка парсинга equipment:", error, booking.equipment);
                parsedEquipment = [];
            }
        } else if (Array.isArray(booking.equipment)) {
            parsedEquipment = booking.equipment;
        }

        setRental(prevRental => ({
            ...prevRental,
            equipment: parsedEquipment
        }));
    }, [booking.equipment]);

    useEffect(() => {
        if (!isEditing) {
            checkConflicts();
        }
    }, [rental.start_date, rental.end_date, rental.status, isEditing]);

    const checkConflicts = () => {
        const newConflicts = [];
        const now = new Date(new Date().toLocaleString('en-US', { timeZone: 'Asia/Almaty' }));

        if (!rental.start_date || !rental.end_date) {
            newConflicts.push({ message: "❌ Укажите дату и время", severity: "error" });
            setConflicts(newConflicts);
            return;
        }

        try {
            const startDateTime = new Date(rental.start_date);
            const endDateTime = new Date(rental.end_date);

            if (isNaN(startDateTime.getTime()) || isNaN(endDateTime.getTime())) {
                newConflicts.push({ message: "❌ Некорректный формат даты/времени", severity: "error" });
            }

            if (startDateTime <= now && rental.status === "Бронь") {
                newConflicts.push({ 
                    message: "⚠️ Дата начала наступила, но статус не 'Выдано'", 
                    severity: "warning" 
                });
            }

            if (endDateTime <= now && rental.status !== "Возвращено") {
                newConflicts.push({ 
                    message: "⚠️ Дата окончания прошла, но статус не 'Возвращено'", 
                    severity: "warning" 
                });
            }

            if (startDateTime >= endDateTime) {
                newConflicts.push({ message: "⚠️ Дата/время начала позже конца!", severity: "error" });
            }

            if (startDateTime > now && (rental.status === "Выдано" || rental.status === "Возвращено")) {
                newConflicts.push({ 
                    message: "⚠️ Аренда выдана или возвращена, но даты еще не наступили", 
                    severity: "warning" 
                });
            }

            setConflicts(newConflicts);
        } catch (error) {
            setConflicts([{ message: "❌ Ошибка проверки дат", severity: "error" }]);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setIsEditing(true);
        setRental(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleBlur = () => {
        setIsEditing(false);
        const updatedRental = {
            ...rental,
            equipment: rental.equipment.map(eq => ({
                name: eq.name,
                quantity: eq.quantity
            }))
        };
        onUpdate(rental.id, updatedRental);
    };

    const formatDateTime = (date) => {
        return new Date(date).toLocaleString("sv-SE").replace(" ", "T").slice(0, 16);
    };

    return (
        <tr>
            <td><input type="text" name="name" value={rental.name} onChange={handleChange} onBlur={handleBlur} /></td>
            <td><input type="text" name="issued_by" value={rental.issued_by} onChange={handleChange} onBlur={handleBlur} /></td>
            <td><input type="text" name="received_by" value={rental.received_by} onChange={handleChange} onBlur={handleBlur} /></td>

            <td className="rental-date-cell">
                <input
                    type="datetime-local"
                    name="start_date"
                    value={rental.start_date ? formatDateTime(rental.start_date) : ""}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    step="900"
                    className="rental-date-input"
                />
                <span className="rental-date-separator">-</span>
                <input
                    type="datetime-local"
                    name="end_date"
                    value={rental.end_date ? formatDateTime(rental.end_date) : ""}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    step="900"
                    className="rental-date-input"
                />
            </td>

            <td>
                <button onClick={() => setShowEquipmentList(true)} className="equipment-button">
                    {rental.equipment.length > 0 ? rental.equipment.map((eq, index) => (
                        <span key={index} className="equipment-tag">
                            {eq.name} × {eq.quantity || 1}
                        </span>
                    )) : "Выбрать оборудование"}
                </button>
                {showEquipmentList && (
                    <EquipmentList
                        selectedEquipment={rental.equipment}
                        onSelect={(selectedEquipment) => {
                            setRental(prevRental => {
                                const updatedRental = { ...prevRental, equipment: selectedEquipment };
                                onUpdate(prevRental.id, updatedRental);
                                return updatedRental;
                            });
                            setShowEquipmentList(false);
                        }}
                        onClose={() => setShowEquipmentList(false)}
                    />
                )}
            </td>

            <td>
                <StatusSelect
                    value={rental.status}
                    onChange={(status) => {
                        setRental(prevRental => {
                            const updatedRental = { ...prevRental, status };
                            onUpdate(prevRental.id, updatedRental);
                            return updatedRental;
                        });
                    }}
                />
            </td>

            <td><input type="text" name="notes" value={rental.notes} onChange={handleChange} onBlur={handleBlur} /></td>

            <td className="conflicts-cell">
                {conflicts.length > 0 ? (
                    <div className="conflicts-list">
                        {conflicts.map((conflict, index) => (
                            <div key={index} className={`conflict-item conflict-${conflict.severity}`}>
                                {conflict.message}
                            </div>
                        ))}
                    </div>
                ) : (
                    <span className="no-conflicts">✓</span>
                )}
            </td>

            <td>
                <button onClick={() => onDelete(rental.id)} className="delete-button" title="Удалить">✕</button>
            </td>
        </tr>
    );
};

export default RentalRow;
