import React from "react";
import DatePicker from "react-datepicker";
import StatusSelect from "./StatusSelect";

function RentalRow({ rental, rowIndex, updateEquipment, rentals, setRentals, setSelectedRow }) {
  return (
    <tr>
      <td className="date-cell">
        <div className="date-block">
          <span>С</span>
          <DatePicker
            selected={rental.startDate}
            onChange={(date) => {
              let newRentals = [...rentals];
              newRentals[rowIndex].startDate = date;
              setRentals(newRentals);
            }}
            showTimeSelect
            dateFormat="dd/MM/yy HH:mm"
          />
        </div>
        <div className="date-block">
          <span>По</span>
          <DatePicker
            selected={rental.endDate}
            onChange={(date) => {
              let newRentals = [...rentals];
              newRentals[rowIndex].endDate = date;
              setRentals(newRentals);
            }}
            showTimeSelect
            dateFormat="dd/MM/yy HH:mm"
          />
        </div>
      </td>
      <td>
        <input
          type="text"
          value={rental.renter}
          onChange={(e) => {
            let newRentals = [...rentals];
            newRentals[rowIndex].renter = e.target.value;
            setRentals(newRentals);
          }}
        />
      </td>
      <td>
        <button onClick={() => setSelectedRow(rowIndex)}>
          {rental.equipment.length > 0 ? rental.equipment.join(", ") : "Добавить"}
        </button>
      </td>
      <td>
        <input
          type="text"
          value={rental.issuer}
          onChange={(e) => {
            let newRentals = [...rentals];
            newRentals[rowIndex].issuer = e.target.value;
            setRentals(newRentals);
          }}
        />
      </td>
      <td>
        <StatusSelect
          status={rental.status}
          onChange={(status) => {
            let newRentals = [...rentals];
            newRentals[rowIndex].status = status;
            setRentals(newRentals);
          }}
        />
      </td>
      <td>{rental.conflicts}</td>
    </tr>
  );
}

export default RentalRow;
