import React, { useEffect, useState, useRef } from "react";
import API_BASE_URL from "../config"; // Импортируем базовый URL

const EquipmentList = ({ onSelect, onClose }) => {
    const [equipment, setEquipment] = useState([]);
    const [loading, setLoading] = useState(true);
    const [selectedItems, setSelectedItems] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const listRef = useRef(null);

    useEffect(() => {
        fetch(`${API_BASE_URL}/inventory`)
            .then((res) => res.json())
            .then((data) => {
                setEquipment(data);
                setLoading(false);
            })
            .catch((error) => {
                console.error("Ошибка загрузки оборудования:", error);
                setLoading(false);
            });

        const handleClickOutside = (event) => {
            if (listRef.current && !listRef.current.contains(event.target)) {
                onClose();
            }
        };

        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, [onClose]);

    const toggleSelection = (item) => {
        setSelectedItems((prevSelected) =>
            prevSelected.some((selected) => selected.id === item.id)
                ? prevSelected.filter((selected) => selected.id !== item.id)
                : [...prevSelected, item]
        );
    };

    const handleConfirm = () => {
        onSelect(selectedItems);
        onClose();
    };

    const filteredEquipment = equipment.filter((item) =>
        item.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div className="modal" ref={listRef}>
            <div className="modal-content">
                <h3>Выберите оборудование</h3>
                <input
                    type="text"
                    placeholder="Поиск..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    style={{ width: "100%", marginBottom: "10px" }}
                />
                {loading ? (
                    <p>Загрузка...</p>
                ) : (
                    <ul style={{ maxHeight: "300px", overflowY: "auto" }}>
                        {filteredEquipment.map((item) => (
                            <li key={item.id}>
                                <label>
                                    <input
                                        type="checkbox"
                                        checked={selectedItems.some((selected) => selected.id === item.id)}
                                        onChange={() => toggleSelection(item)}
                                    />
                                    {item.name} | {item.available > 0 ? `Доступно: ${item.available}` : "❌ Нет в наличии"}
                                    {item.next_available && ` | Освободится: ${item.next_available}`}
                                </label>
                            </li>
                        ))}
                    </ul>
                )}
                <button onClick={handleConfirm}>Готово</button>
                <button onClick={onClose}>Закрыть</button>
            </div>
        </div>
    );
};

export default EquipmentList;
