import React, { useState, useEffect } from "react";
import RentalRow from "./RentalRow";
import API_BASE_URL from "../config";
import './Table.css';

const Table = () => {
    const [bookings, setBookings] = useState([]);
    const [inventory, setInventory] = useState([]); // <-- –î–æ–±–∞–≤–∏–ª–∏ –∏–Ω–≤–µ–Ω—Ç–∞—Ä—å
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchBookings();
        fetchInventory(); // <-- –ó–∞–≥—Ä—É–∂–∞–µ–º –∏–Ω–≤–µ–Ω—Ç–∞—Ä—å
    }, []);

    const fetchBookings = async () => {
        try {
            const response = await fetch(`${API_BASE_URL}/bookings`);
            if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
            const data = await response.json();

            setBookings(Array.isArray(data) ? 
                data.map(booking => ({
                    ...booking,
                    equipment: Array.isArray(booking.equipment) ? booking.equipment : []
                })) 
                : []
            );
        } catch (err) {
            console.error("Error loading bookings:", err);
            setError(err.message);
        }
    };

    const fetchInventory = async () => {
        try {
            const response = await fetch(`${API_BASE_URL}/inventory`); // <-- –ó–∞–≥—Ä—É–∂–∞–µ–º –∏–Ω–≤–µ–Ω—Ç–∞—Ä—å
            if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
            const data = await response.json();
            setInventory(Array.isArray(data) ? data : []); // <-- –°–æ—Ö—Ä–∞–Ω—è–µ–º —Ç–æ–ª—å–∫–æ –º–∞—Å—Å–∏–≤
        } catch (err) {
            console.error("Error loading inventory:", err);
            setError(err.message);
        }
    };

    return (
        <div>
            <h2>üìã –¢–∞–±–ª–∏—Ü–∞ –∞—Ä–µ–Ω–¥—ã</h2>
            {error && <p className="error-message">{error}</p>}
            <button onClick={fetchBookings} className="add-button">üîÑ –û–±–Ω–æ–≤–∏—Ç—å –¥–∞–Ω–Ω—ã–µ</button>
            <table className="rental-table">
                <thead>
                    <tr>
                        <th className="table-header">–î–∞—Ç–∞ –∞—Ä–µ–Ω–¥—ã</th>
                        <th className="table-header">–ò–º—è</th>
                        <th className="table-header">–ö—Ç–æ –≤—ã–¥–∞–ª</th>
                        <th className="table-header">–ö—Ç–æ –ø—Ä–∏–Ω—è–ª</th>
                        <th className="table-header">–û–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏–µ</th>
                        <th className="table-header">–ü—Ä–∏–º–µ—á–∞–Ω–∏—è</th>
                        <th className="table-header">–°—Ç–∞—Ç—É—Å</th>
                        <th className="table-header">–ö–æ–Ω—Ñ–ª–∏–∫—Ç—ã</th>
                        <th className="table-header">–î–µ–π—Å—Ç–≤–∏—è</th>
                    </tr>
                </thead>
                <tbody>
                    {bookings.map((booking) => (
                        <RentalRow 
                            key={booking.id}
                            booking={booking}
                            inventory={inventory} // <-- –ü–µ—Ä–µ–¥–∞–µ–º inventory –≤ RentalRow
                        />
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Table;
