import React, { useState, useEffect } from "react";
import EquipmentList from "./EquipmentList";
import StatusSelect from "./StatusSelect";
import './RentalRow.css';

const RentalRow = ({ booking, onUpdate, onDelete }) => {
    const [rental, setRental] = useState(booking);
    const [showEquipmentList, setShowEquipmentList] = useState(false);
    const [conflicts, setConflicts] = useState([]);

    useEffect(() => {
        setRental(booking);
    }, [booking]);

    useEffect(() => {
        checkConflicts();
    }, [rental.start_date, rental.end_date, rental.status]);

    const checkConflicts = () => {
        const newConflicts = [];
        const now = new Date();

        if (!rental.start_date || !rental.end_date) {
            newConflicts.push({ message: "❌ Укажите дату и время", severity: "error" });
            setConflicts(newConflicts);
            return;
        }

        try {
            const startDateTime = new Date(rental.start_date);
            const endDateTime = new Date(rental.end_date);

            if (isNaN(startDateTime.getTime()) || isNaN(endDateTime.getTime())) {
                newConflicts.push({ message: "❌ Некорректный формат даты/времени", severity: "error" });
            }

            // Проверка на наступившую дату начала без статуса "Выдано"
            if (startDateTime <= now && rental.status === "Бронь") {
                newConflicts.push({ 
                    message: "⚠️ Дата начала наступила, но статус не 'Выдано'", 
                    severity: "warning" 
                });
            }

            // Проверка на наступившую дату окончания без статуса "Возвращено"
            if (endDateTime <= now && rental.status !== "Возвращено") {
                newConflicts.push({ 
                    message: "⚠️ Дата окончания прошла, но статус не 'Возвращено'", 
                    severity: "warning" 
                });
            }

            if (startDateTime >= endDateTime) {
                newConflicts.push({ message: "⚠️ Дата/время начала позже конца!", severity: "error" });
            }

            // Проверка кратности времени 15 минутам
            const startMinutes = startDateTime.getMinutes();
            const endMinutes = endDateTime.getMinutes();
            if (startMinutes % 15 !== 0 || endMinutes % 15 !== 0) {
                newConflicts.push({ 
                    message: "ℹ️ Время должно быть кратно 15 минутам", 
                    severity: "info" 
                });
            }

            setConflicts(newConflicts);
        } catch (error) {
            setConflicts([{ message: "❌ Ошибка проверки дат", severity: "error" }]);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        
        if (name === 'start_date' || name === 'end_date') {
            const date = new Date(value);
            if (!isNaN(date.getTime())) {
                // Корректируем часовой пояс
                const userTimezoneOffset = date.getTimezoneOffset() * 60000;
                const adjustedDate = new Date(date.getTime() - userTimezoneOffset);
                
                // Округляем минуты до ближайших 15
                const minutes = adjustedDate.getMinutes();
                const roundedMinutes = Math.round(minutes / 15) * 15;
                adjustedDate.setMinutes(roundedMinutes);
                
                // Форматируем дату в строку ISO с учетом локального времени
                const roundedValue = adjustedDate.toISOString().slice(0, 16);
                
                const updatedRental = { ...rental, [name]: roundedValue };
                setRental(updatedRental);
                if (onUpdate) {
                    onUpdate(rental.id, updatedRental);
                }
                return;
            }
        }

        const updatedRental = { ...rental, [name]: value };
        setRental(updatedRental);
        if (onUpdate) {
            onUpdate(rental.id, updatedRental);
        }
    };

    const formatDateTimeForInput = (dateString) => {
        if (!dateString) return "";
        const date = new Date(dateString);
        if (isNaN(date.getTime())) return "";
        
        // Корректируем часовой пояс
        const userTimezoneOffset = date.getTimezoneOffset() * 60000;
        const adjustedDate = new Date(date.getTime() - userTimezoneOffset);
        return adjustedDate.toISOString().slice(0, 16);
    };

    return (
        <tr>
            <td className="rental-date-cell">
                <input
                    type="datetime-local"
                    name="start_date"
                    value={formatDateTimeForInput(rental.start_date)}
                    onChange={handleChange}
                    step="900"
                    className="rental-date-input"
                    min={new Date().toISOString().slice(0, 16)}
                />
                <span className="rental-date-separator">-</span>
                <input
                    type="datetime-local"
                    name="end_date"
                    value={formatDateTimeForInput(rental.end_date)}
                    onChange={handleChange}
                    step="900"
                    className="rental-date-input"
                    min={rental.start_date || new Date().toISOString().slice(0, 16)}
                />
            </td>

            <td>
                <input 
                    type="text" 
                    name="renter" 
                    value={rental.renter || ''} 
                    onChange={handleChange}
                />
            </td>

            <td>
                <input 
                    type="text" 
                    name="issuer" 
                    value={rental.issuer || ''} 
                    onChange={handleChange}
                />
            </td>

            <td>
                <input 
                    type="text" 
                    name="receiver" 
                    value={rental.receiver || ''} 
                    onChange={handleChange}
                />
            </td>

            <td>
                <button 
                    onClick={() => setShowEquipmentList(true)} 
                    className="equipment-button"
                >
                    {rental.equipment && rental.equipment.length > 0
                        ? rental.equipment.map((eq) => (
                            <span
                                key={eq.id}
                                className="equipment-tag"
                            >
                                {eq.name} × {eq.quantity || 1}
                            </span>
                          ))
                        : "Выбрать оборудование"}
                </button>
                {showEquipmentList && (
                    <EquipmentList
                        selectedEquipment={rental.equipment}
                        onSelect={(selectedEquipment) => {
                            const updatedRental = { ...rental, equipment: selectedEquipment };
                            setRental(updatedRental);
                            if (onUpdate) {
                                onUpdate(rental.id, updatedRental);
                            }
                            setShowEquipmentList(false);
                        }}
                        onClose={() => setShowEquipmentList(false)}
                    />
                )}
            </td>

            <td>
                <input 
                    type="text" 
                    name="notes" 
                    value={rental.notes || ''} 
                    onChange={handleChange}
                />
            </td>

            <td>
                <StatusSelect
                    value={rental.status}
                    onChange={(status) => {
                        const updatedRental = { ...rental, status };
                        setRental(updatedRental);
                        if (onUpdate) {
                            onUpdate(rental.id, updatedRental);
                        }
                    }}
                />
            </td>

            <td className="conflicts-cell">
                {conflicts.length > 0 ? (
                    <div className="conflicts-list">
                        {conflicts.map((conflict, index) => (
                            <div 
                                key={index} 
                                className={`conflict-item conflict-${conflict.severity}`}
                            >
                                {conflict.message}
                            </div>
                        ))}
                    </div>
                ) : (
                    <span className="no-conflicts">✓</span>
                )}
            </td>

            <td>
                <button 
                    onClick={() => onDelete(rental.id)}
                    className="delete-button"
                >
                    ❌
                </button>
            </td>
        </tr>
    );
};

export default RentalRow;
