// Функция для получения цвета по категории оборудования
export const getCategoryColor = (category) => {
    const colors = {
        // Камеры и объективы
        'Камера': '#e74c3c',
        'Объектив': '#1abc9c',

        // Звук
        'Звук': '#9b59b6',

        // Свет
        'Свет': '#f39c12',
        'Насадка на свет': '#f1c40f',

        // Аксессуары
        'Аксессуары для съемок': '#34495e',
        'Примочки к камерам': '#27ae60',
        'Сумки': '#8e44ad',

        // Стойки и штативы
        'Стойки и штативы': '#95a5a6',

        // Провода и зарядки
        'Провода и зарядки': '#16a085',

        // Компьютерная техника
        'Компьютерная техника': '#3498db',

        // Прочее
        'Прочее': '#7f8c8d',
        'Мебель': '#d35400',
        'Бытовая техника': '#c0392b',
        'тест': '#ff6b35'
    };

    return colors[category] || '#95a5a6'; // Серый цвет по умолчанию
};
