import React, { useState, useEffect } from "react";
import EquipmentList from "./EquipmentList";
import StatusSelect from "./StatusSelect";
import './RentalRow.css';

const RentalRow = ({ booking, onUpdate, onDelete, onSave }) => {
    const [rental, setRental] = useState({
        ...booking,
        equipment: Array.isArray(booking.equipment) ? booking.equipment : []
    });
    const [showEquipmentList, setShowEquipmentList] = useState(false);
    const [conflicts, setConflicts] = useState([]);
    const [isEditing, setIsEditing] = useState(false);

    useEffect(() => {
        setRental({
            ...booking,
            equipment: Array.isArray(booking.equipment) ? booking.equipment : []
        });
    }, [booking]);

    useEffect(() => {
        if (!isEditing) {
            checkConflicts();
        }
    }, [rental.start_date, rental.end_date, rental.status, isEditing]);

    const checkConflicts = () => {
        const newConflicts = [];
        const now = new Date(new Date().toLocaleString('en-US', { timeZone: 'Asia/Almaty' }));
    
        if (!rental.start_date || !rental.end_date) {
            newConflicts.push({ message: "❌ Укажите дату и время", severity: "error" });
            setConflicts(newConflicts);
            return;
        }
    
        try {
            const startDateTime = new Date(rental.start_date);
            const endDateTime = new Date(rental.end_date);
    
            if (isNaN(startDateTime.getTime()) || isNaN(endDateTime.getTime())) {
                newConflicts.push({ message: "❌ Некорректный формат даты/времени", severity: "error" });
            }
    
            if (startDateTime <= now && rental.status === "Бронь") {
                newConflicts.push({ 
                    message: "⚠️ Дата начала наступила, но статус не 'Выдано'", 
                    severity: "warning" 
                });
            }
    
            if (endDateTime <= now && rental.status !== "Возвращено") {
                newConflicts.push({ 
                    message: "⚠️ Дата окончания прошла, но статус не 'Возвращено'", 
                    severity: "warning" 
                });
            }
    
            if (startDateTime >= endDateTime) {
                newConflicts.push({ message: "⚠️ Дата/время начала позже конца!", severity: "error" });
            }
    
            // Новый конфликт: аренда выдана или возвращена, но даты еще не наступили
            if (startDateTime > now && (rental.status === "Выдано" || rental.status === "Возвращено")) {
                newConflicts.push({ 
                    message: "⚠️ Аренда выдана или возвращена, но даты еще не наступили", 
                    severity: "warning" 
                });
            }
    
            // Пример дополнительного конфликта: проверка на пересечение с другими бронированиями
            const overlappingBookings = bookings.filter(b => 
                b.id !== rental.id && 
                ((new Date(b.start_date) <= endDateTime && new Date(b.end_date) >= startDateTime))
            );
    
            if (overlappingBookings.length > 0) {
                newConflicts.push({ 
                    message: "⚠️ Пересечение с другими бронированиями", 
                    severity: "warning" 
                });
            }
    
            setConflicts(newConflicts);
        } catch (error) {
            setConflicts([{ message: "❌ Ошибка проверки дат", severity: "error" }]);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setIsEditing(true);
        setRental(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleBlur = () => {
        setIsEditing(false);
        
        if (rental.id.toString().length === 13) {
            if (onSave) {
                onSave(rental.id, rental);
            }
        } else {
            onUpdate(rental.id, rental);
        }
    };

    const renderInput = (name, value) => (
        <input 
            type="text" 
            name={name}
            value={value || ''} 
            onChange={handleChange}
            onBlur={handleBlur}
            className="rental-input"
        />
    );

    return (
        <tr>
            <td className="rental-date-cell">
                <input
                    type="datetime-local"
                    name="start_date"
                    value={rental.start_date ? rental.start_date.slice(0, 16) : ""}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    step="900"
                    className="rental-date-input"
                />
                <span className="rental-date-separator">-</span>
                <input
                    type="datetime-local"
                    name="end_date"
                    value={rental.end_date ? rental.end_date.slice(0, 16) : ""}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    step="900"
                    className="rental-date-input"
                />
            </td>

            <td>{renderInput("renter", rental.renter)}</td>
            <td>{renderInput("issuer", rental.issuer)}</td>
            <td>{renderInput("receiver", rental.receiver)}</td>

            <td>
                <button 
                    onClick={() => setShowEquipmentList(true)} 
                    className="equipment-button"
                >
                    {rental.equipment && Array.isArray(rental.equipment) && rental.equipment.length > 0
                        ? rental.equipment.map((eq) => (
                            <span
                                key={eq.id}
                                className="equipment-tag"
                            >
                                {eq.name} × {eq.quantity || 1}
                            </span>
                          ))
                        : "Выбрать оборудование"}
                </button>
                {showEquipmentList && (
                    <EquipmentList
                        selectedEquipment={Array.isArray(rental.equipment) ? rental.equipment : []}
                        onSelect={(selectedEquipment) => {
                            const updatedRental = { ...rental, equipment: selectedEquipment };
                            setRental(updatedRental);
                            if (onUpdate && rental.id.toString().length !== 13) {
                                onUpdate(rental.id, updatedRental);
                            }
                            setShowEquipmentList(false);
                        }}
                        onClose={() => setShowEquipmentList(false)}
                    />
                )}
            </td>

            <td>{renderInput("notes", rental.notes)}</td>

            <td>
                <StatusSelect
                    value={rental.status}
                    onChange={(status) => {
                        const updatedRental = { ...rental, status };
                        setRental(updatedRental);
                        if (onUpdate && rental.id.toString().length !== 13) {
                            onUpdate(rental.id, updatedRental);
                        }
                    }}
                />
            </td>

            <td className="conflicts-cell">
                {conflicts.length > 0 ? (
                    <div className="conflicts-list">
                        {conflicts.map((conflict, index) => (
                            <div 
                                key={index} 
                                className={`conflict-item conflict-${conflict.severity}`}
                            >
                                {conflict.message}
                            </div>
                        ))}
                    </div>
                ) : (
                    <span className="no-conflicts">✓</span>
                )}
            </td>

            <td>
                <div className="action-buttons">
                    <button 
                        onClick={() => onDelete(rental.id)}
                        className="delete-button"
                        title="Удалить"
                    >
                        ✕
                    </button>
                </div>
            </td>
        </tr>
    );
};

export default RentalRow;
