import React, { useState, useEffect } from "react";
import EquipmentList from "./EquipmentList";
import StatusSelect from "./StatusSelect";
import './RentalRow.css';

const RentalRow = ({ booking, inventory, bookings, onUpdate, onDelete }) => {
    const [rental, setRental] = useState({
        ...booking,
        equipment: Array.isArray(booking.equipment) ? booking.equipment : []
    });

    const [showEquipmentList, setShowEquipmentList] = useState(false);
    const [conflicts, setConflicts] = useState([]);
    const [isEditing, setIsEditing] = useState(false);

    useEffect(() => {
        let parsedEquipment = [];
        try {
            parsedEquipment = JSON.parse(booking.equipment);
        } catch (e) {
            parsedEquipment = booking.equipment;
        }

        setRental(prevRental => {
            if (JSON.stringify(prevRental.equipment) !== JSON.stringify(parsedEquipment)) {
                return { ...prevRental, equipment: parsedEquipment };
            }
            return prevRental;
        });

    }, [booking.equipment]);

    useEffect(() => {
        if (!isEditing) {
            checkConflicts();
        }
    }, [rental.start_date, rental.end_date, rental.status, isEditing]);

    const checkConflicts = () => {
        const newConflicts = [];
        const now = new Date(new Date().toLocaleString('en-US', { timeZone: 'Asia/Almaty' }));

        if (!rental.start_date || !rental.end_date) {
            newConflicts.push({ message: "❌ Укажите дату и время", severity: "error" });
            setConflicts(newConflicts);
            return;
        }

        try {
            const startDateTime = new Date(rental.start_date);
            const endDateTime = new Date(rental.end_date);

            if (isNaN(startDateTime.getTime()) || isNaN(endDateTime.getTime())) {
                newConflicts.push({ message: "❌ Некорректный формат даты/времени", severity: "error" });
            }

            if (startDateTime.getTime() <= now.getTime() && rental.status === "Бронь") {
                newConflicts.push({ 
                    message: "⚠️ Дата начала наступила, но статус не 'Выдано'", 
                    severity: "warning" 
                });
            }

            if (endDateTime.getTime() <= now.getTime() && rental.status !== "Возвращено") {
                newConflicts.push({ 
                    message: "⚠️ Дата окончания прошла, но статус не 'Возвращено'", 
                    severity: "warning" 
                });
            }

            if (startDateTime.getTime() >= endDateTime.getTime()) {
                newConflicts.push({ message: "⚠️ Дата/время начала позже конца!", severity: "error" });
            }

            if (startDateTime.getTime() > now.getTime() && (rental.status === "Выдано" || rental.status === "Возвращено")) {
                newConflicts.push({ 
                    message: "⚠️ Аренда выдана или возвращена, но даты еще не наступили", 
                    severity: "warning" 
                });
            }

            setConflicts(newConflicts);
        } catch (error) {
            setConflicts([{ message: "❌ Ошибка проверки дат", severity: "error" }]);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setIsEditing(true);
        setRental(prev => ({ ...prev, [name]: value }));
    };

    const handleBlur = () => {
        setIsEditing(false);
        onUpdate(rental.id, rental);
    };

    return (
        <tr>
            <td>
                <input type="text" name="renter" value={rental.renter || ""} onChange={handleChange} onBlur={handleBlur} placeholder="Имя арендатора" />
            </td>
            <td>
                <input type="text" name="issuer" value={rental.issuer || ""} onChange={handleChange} onBlur={handleBlur} placeholder="Кто выдал" />
            </td>
            <td>
                <input type="text" name="receiver" value={rental.receiver || ""} onChange={handleChange} onBlur={handleBlur} placeholder="Кто принял" />
            </td>
            <td>
                <input type="text" name="notes" value={rental.notes || ""} onChange={handleChange} onBlur={handleBlur} placeholder="Примечания" />
            </td>
            <td>
                <StatusSelect value={rental.status} onChange={(status) => setRental(prev => ({ ...prev, status }))} />
            </td>
            <td>
                {conflicts.length > 0 ? conflicts.map((c, index) => <div key={index}>{c.message}</div>) : "✓"}
            </td>
            <td>
                <button onClick={() => onDelete(rental.id)}>✕</button>
            </td>
        </tr>
    );
};

export default RentalRow;
