import React, { useState, useEffect } from 'react';

const InventoryTable = () => {
    const [inventory, setInventory] = useState([]);
    const [loading, setLoading] = useState(true);

    // Загрузка данных с сервера
    useEffect(() => {
        const fetchInventory = async () => {
            try {
                const response = await fetch("http://localhost:5000/api/inventory"); // Замените на правильный URL, если нужно
                if (!response.ok) {
                    throw new Error(`Ошибка загрузки: ${response.status}`);
                }
                const data = await response.json();
                setInventory(data);
            } catch (error) {
                console.error('Ошибка загрузки инвентаря:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchInventory();
    }, []);

    return (
        <div>
            <h2>Инвентарь</h2>
            {loading ? (
                <p>Загрузка...</p>
            ) : (
                <table>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Название</th>
                            <th>Категория</th>
                            <th>Подкатегория</th>
                            <th>Модель</th>
                            <th>Серийный номер</th>
                            <th>Примечания</th>
                            <th>Статус</th>
                            <th>Общее количество</th>
                            <th>Кому принадлежит</th>
                        </tr>
                    </thead>
                    <tbody>
                        {inventory.map((item) => (
                            <tr key={item.id}>
                                <td>{item.id}</td>
                                <td>{item.name}</td>
                                <td>{item.category}</td>
                                <td>{item.subcategory}</td>
                                <td>{item.model}</td>
                                <td>{item.serial_number}</td>
                                <td>{item.notes}</td>
                                <td>{item.status}</td>
                                <td>{item.total}</td>
                                <td>{item.belongs_to}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default InventoryTable;
