import React, { useState, useEffect } from "react";
import EquipmentList from "./EquipmentList";
import StatusSelect from "./StatusSelect";
import "./RentalRow.css";

const RentalRow = ({ booking, onUpdate, onDelete }) => {
    const [rental, setRental] = useState(booking);
    const [showEquipmentList, setShowEquipmentList] = useState(false);
    const [conflicts, setConflicts] = useState([]);

    useEffect(() => {
        setRental(booking);
    }, [booking]);

    useEffect(() => {
        checkConflicts();
    }, [rental.start_date, rental.end_date, rental.status]);

    const checkConflicts = () => {
        const newConflicts = [];
        const now = new Date();

        if (!rental.start_date || !rental.end_date) {
            newConflicts.push("❌ Укажите дату и время");
        } else {
            const startDateTime = new Date(rental.start_date);
            const endDateTime = new Date(rental.end_date);

            if (startDateTime >= endDateTime) {
                newConflicts.push("⚠️ Дата начала позже даты окончания!");
            }
            if (startDateTime <= now && rental.status === "Бронь") {
                newConflicts.push("⚠️ Дата начала прошла, но статус не 'Выдано'");
            }
            if (endDateTime <= now && rental.status !== "Возвращено") {
                newConflicts.push("⚠️ Дата окончания прошла, но статус не 'Возвращено'");
            }
            if (startDateTime > now && ["Выдано", "Возвращено"].includes(rental.status)) {
                newConflicts.push("⚠️ Аренда выдана или возвращена, но даты еще не наступили");
            }
        }

        setConflicts(newConflicts);
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setRental(prev => {
            const updatedRental = { ...prev, [name]: value };
            onUpdate(prev.id, updatedRental);
            return updatedRental;
        });
    };

    return (
        <tr>
            <td className="rental-date-cell">
                <input type="datetime-local" name="start_date" value={rental.start_date || ""} onChange={handleChange} step="900" className="rental-date-input" />
                <span className="rental-date-separator">-</span>
                <input type="datetime-local" name="end_date" value={rental.end_date || ""} onChange={handleChange} step="900" className="rental-date-input" />
            </td>
            <td><input type="text" name="renter" value={rental.renter || ""} onChange={handleChange} className="rental-input" /></td>
            <td><input type="text" name="issuer" value={rental.issuer || ""} onChange={handleChange} className="rental-input" /></td>
            <td><input type="text" name="receiver" value={rental.receiver || ""} onChange={handleChange} className="rental-input" /></td>
            <td>
                <button onClick={() => setShowEquipmentList(true)} className="equipment-button">
                    {rental.equipment.length > 0 ? rental.equipment.map(eq => (
                        <span key={eq.id} className="equipment-tag">{eq.name} × {eq.quantity || 1}</span>
                    )) : "Выбрать оборудование"}
                </button>
                {showEquipmentList && (
                    <EquipmentList
                        selectedEquipment={rental.equipment}
                        onSelect={(selectedEquipment) => {
                            setRental(prev => {
                                const updatedRental = { ...prev, equipment: selectedEquipment };
                                onUpdate(prev.id, updatedRental);
                                return updatedRental;
                            });
                            setShowEquipmentList(false);
                        }}
                        onClose={() => setShowEquipmentList(false)}
                    />
                )}
            </td>
            <td><input type="text" name="notes" value={rental.notes || ""} onChange={handleChange} className="rental-input" /></td>
            <td>
                <StatusSelect
                    value={rental.status}
                    onChange={(status) => {
                        setRental(prev => {
                            const updatedRental = { ...prev, status };
                            onUpdate(prev.id, updatedRental);
                            return updatedRental;
                        });
                    }}
                />
            </td>
            <td className="conflicts-cell">
                {conflicts.length > 0 ? (
                    <div className="conflicts-list">
                        {conflicts.map((conflict, index) => (
                            <div key={index} className="conflict-item">{conflict}</div>
                        ))}
                    </div>
                ) : (
                    <span className="no-conflicts">✓</span>
                )}
            </td>
            <td>
                <button onClick={() => onDelete(rental.id)} className="delete-button" title="Удалить">✕</button>
            </td>
        </tr>
    );
};

export default RentalRow;
