import React, { useState, useEffect } from "react";

const Inventory = () => {
    const [inventory, setInventory] = useState([]);
    const [loading, setLoading] = useState(true);

    // Загрузка инвентаря
    useEffect(() => {
        fetch("/api/inventory")
            .then((res) => res.json()) // Преобразуем ответ в формат JSON
            .then((data) => {
                setInventory(data); // Заполняем данные инвентаря
                setLoading(false); // Снимаем индикатор загрузки
            })
            .catch((error) => {
                console.error("Error fetching inventory:", error);
                setLoading(false); // Завершаем загрузку, даже если ошибка
            });
    }, []);

    if (loading) {
        return <p>Загрузка...</p>;
    }

    return (
        <div>
            <h2>Инвентарь</h2>
            <table>
                <thead>
                    <tr>
                        <th>Название</th>
                        <th>Категория</th>
                        <th>Подкатегория</th>
                        <th>Модель</th>
                        <th>Серийный номер</th>
                        <th>Примечания</th>
                        <th>Статус</th>
                        <th>Общее количество</th>
                        <th>Кому принадлежит</th>
                    </tr>
                </thead>
                <tbody>
                    {inventory.map((item) => (
                        <tr key={item.id}>
                            <td>{item.name}</td>
                            <td>{item.category}</td>
                            <td>{item.subcategory}</td>
                            <td>{item.model}</td>
                            <td>{item.serial_number}</td>
                            <td>{item.notes}</td>
                            <td>{item.status}</td>
                            <td>{item.total}</td>
                            <td>{item.belongs_to}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Inventory;
