import React, { useState, useEffect } from "react";
import EquipmentList from "./EquipmentList";
import StatusSelect from "./StatusSelect";
import './RentalRow.css';

const RentalRow = ({ booking, onUpdate, onDelete }) => {
    const [rental, setRental] = useState(booking);
    const [showEquipmentList, setShowEquipmentList] = useState(false);
    const [conflict, setConflict] = useState({ message: "", severity: "none" });

    useEffect(() => {
        setRental(booking);
    }, [booking]);

    useEffect(() => {
        checkConflicts();
    }, [rental.start_date, rental.end_date]);

    const checkConflicts = () => {
        if (!rental.start_date || !rental.end_date) {
            setConflict({ message: "❌ Укажите дату и время", severity: "error" });
            return;
        }

        try {
            const startDateTime = new Date(rental.start_date);
            const endDateTime = new Date(rental.end_date);
            const now = new Date();

            if (isNaN(startDateTime.getTime()) || isNaN(endDateTime.getTime())) {
                setConflict({ message: "❌ Некорректный формат даты/времени", severity: "error" });
                return;
            }

            if (startDateTime < now) {
                setConflict({ message: "⚠️ Дата начала в прошлом", severity: "warning" });
                return;
            }

            if (startDateTime >= endDateTime) {
                setConflict({ message: "⚠️ Дата/время начала позже конца!", severity: "error" });
                return;
            }

            const minDuration = 30 * 60 * 1000;
            if (endDateTime - startDateTime < minDuration) {
                setConflict({ message: "ℹ️ Минимальная длительность - 30 минут", severity: "info" });
                return;
            }

            setConflict({ message: "", severity: "none" });
        } catch (error) {
            setConflict({ message: "❌ Ошибка проверки дат", severity: "error" });
        }
    };

    const handleChange = (e) => {
        const updatedRental = { ...rental, [e.target.name]: e.target.value };
        setRental(updatedRental);
        if (onUpdate) {
            onUpdate(rental.id, updatedRental);
        }
    };

    return (
        <tr>
            <td className="rental-date-cell">
                <input
                    type="datetime-local"
                    name="start_date"
                    value={rental.start_date ? rental.start_date.slice(0, 16) : ""}
                    onChange={handleChange}
                    className="rental-date-input"
                />
                <span className="rental-date-separator">-</span>
                <input
                    type="datetime-local"
                    name="end_date"
                    value={rental.end_date ? rental.end_date.slice(0, 16) : ""}
                    onChange={handleChange}
                    className="rental-date-input"
                />
            </td>

            <td>
                <input 
                    type="text" 
                    name="renter" 
                    value={rental.renter || ''} 
                    onChange={handleChange}
                />
            </td>

            <td>
                <input 
                    type="text" 
                    name="issuer" 
                    value={rental.issuer || ''} 
                    onChange={handleChange}
                />
            </td>

            <td>
                <input 
                    type="text" 
                    name="receiver" 
                    value={rental.receiver || ''} 
                    onChange={handleChange}
                />
            </td>

            <td>
                <button 
                    onClick={() => setShowEquipmentList(true)} 
                    className="equipment-button"
                >
                    {rental.equipment && rental.equipment.length > 0
                        ? rental.equipment.map((eq) => (
                            <span
                                key={eq.id}
                                className="equipment-tag"
                            >
                                {eq.name} × {eq.quantity || 1}
                            </span>
                          ))
                        : "Выбрать оборудование"}
                </button>
                {showEquipmentList && (
                    <EquipmentList
                        selectedEquipment={rental.equipment}
                        onSelect={(selectedEquipment) => {
                            const updatedRental = { ...rental, equipment: selectedEquipment };
                            setRental(updatedRental);
                            if (onUpdate) {
                                onUpdate(rental.id, updatedRental);
                            }
                            setShowEquipmentList(false);
                        }}
                        onClose={() => setShowEquipmentList(false)}
                    />
                )}
            </td>

            <td>
                <input 
                    type="text" 
                    name="notes" 
                    value={rental.notes || ''} 
                    onChange={handleChange}
                />
            </td>

            <td>
                <StatusSelect
                    value={rental.status}
                    onChange={(status) => {
                        const updatedRental = { ...rental, status };
                        setRental(updatedRental);
                        if (onUpdate) {
                            onUpdate(rental.id, updatedRental);
                        }
                    }}
                />
            </td>

            <td className={`conflict-cell conflict-${conflict.severity}`}>
                {conflict.message || "✓"}
            </td>

            <td>
                <button 
                    onClick={() => onDelete(rental.id)}
                    className="delete-button"
                >
                    ❌
                </button>
            </td>
        </tr>
    );
};

export default RentalRow;
