import React, { useState, useEffect } from "react";

const EquipmentList = ({ onSelect, onClose }) => {
    const [equipment, setEquipment] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetch("http://127.0.0.1:5000/api/inventory")
            .then((res) => res.json())
            .then((data) => {
                setEquipment(data);
                setLoading(false);
            })
            .catch((err) => {
                console.error("Ошибка загрузки оборудования:", err);
                setError("Не удалось загрузить данные");
                setLoading(false);
            });
    }, []);

    if (loading) return <div>Загрузка...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div className="equipment-modal">
            <h3>Выберите оборудование</h3>
            <ul>
                {equipment.map((item) => (
                    <li key={item.id}>
                        <button onClick={() => onSelect(item.name)}>
                            {item.name} | {item.rented ? `Освободится: ${item.available_date}` : "Доступно"} | Всего: {item.total}
                        </button>
                    </li>
                ))}
            </ul>
            <button onClick={onClose}>Закрыть</button>
        </div>
    );
};

export default EquipmentList;
