import React, { useState } from "react";
import Calendar from "./Calendar";
import EquipmentList from "./EquipmentList";
import StatusSelect from "./StatusSelect";
import "./Table.css";

function Table() {
  const [rentals, setRentals] = useState([]);
  const [showCalendar, setShowCalendar] = useState(false);
  const [selectedRow, setSelectedRow] = useState(null);

  const addRental = () => {
    setRentals([...rentals, { id: rentals.length + 1, date: "", renter: "", equipment: "", issuer: "", receiver: "", status: "Бронь", notes: "" }]);
  };

  return (
    <div className="table-container">
      <button onClick={addRental}>+ Добавить аренду</button>
      <table>
        <thead>
          <tr>
            <th>Дата аренды</th>
            <th>Кто берет</th>
            <th>Оборудование</th>
            <th>Кто выдал</th>
            <th>Кто принял</th>
            <th>Статус</th>
            <th>Примечания</th>
          </tr>
        </thead>
        <tbody>
          {rentals.map((rental, index) => (
            <tr key={index}>
              <td onClick={() => { setShowCalendar(true); setSelectedRow(index); }}>{rental.date || "Выбрать дату"}</td>
              <td><input type="text" value={rental.renter} onChange={(e) => {
                let newRentals = [...rentals];
                newRentals[index].renter = e.target.value;
                setRentals(newRentals);
              }} /></td>
              <td onClick={() => alert("Открыть список оборудования")}>{rental.equipment || "Выбрать"}</td>
              <td><input type="text" value={rental.issuer} onChange={(e) => {
                let newRentals = [...rentals];
                newRentals[index].issuer = e.target.value;
                setRentals(newRentals);
              }} /></td>
              <td><input type="text" value={rental.receiver} onChange={(e) => {
                let newRentals = [...rentals];
                newRentals[index].receiver = e.target.value;
                setRentals(newRentals);
              }} /></td>
              <td><StatusSelect status={rental.status} onChange={(status) => {
                let newRentals = [...rentals];
                newRentals[index].status = status;
                setRentals(newRentals);
              }} /></td>
              <td><input type="text" value={rental.notes} onChange={(e) => {
                let newRentals = [...rentals];
                newRentals[index].notes = e.target.value;
                setRentals(newRentals);
              }} /></td>
            </tr>
          ))}
        </tbody>
      </table>
      {showCalendar && <Calendar onClose={() => setShowCalendar(false)} />}
    </div>
  );
}

export default Table;
