import React, { useState, useEffect } from "react";
import DatePicker from "react-datepicker";
import StatusSelect from "./StatusSelect";
import EquipmentSelect from "./EquipmentSelect";
import "react-datepicker/dist/react-datepicker.css";
import "./styles.css";

function Table({ debugMode }) {
  const [rentals, setRentals] = useState([]);
  const [equipmentList, setEquipmentList] = useState([]);
  const [selectedRow, setSelectedRow] = useState(null);
  const [columnWidths, setColumnWidths] = useState({});
  const [rowHeights, setRowHeights] = useState({});

  useEffect(() => {
    console.time("Загрузка оборудования");
    fetch("/api/inventory")
      .then((res) => res.json())
      .then((data) => {
        console.timeEnd("Загрузка оборудования");
        setEquipmentList(data);
      })
      .catch((err) => console.error("Ошибка загрузки оборудования:", err));

    // Загружаем размеры из localStorage
    setColumnWidths(JSON.parse(localStorage.getItem("columnWidths")) || {});
    setRowHeights(JSON.parse(localStorage.getItem("rowHeights")) || {});
  }, []);

  const saveColumnWidth = (index, width) => {
    const updatedWidths = { ...columnWidths, [index]: width };
    setColumnWidths(updatedWidths);
    localStorage.setItem("columnWidths", JSON.stringify(updatedWidths));
  };

  const saveRowHeight = (index, height) => {
    const updatedHeights = { ...rowHeights, [index]: height };
    setRowHeights(updatedHeights);
    localStorage.setItem("rowHeights", JSON.stringify(updatedHeights));
  };

  const addRental = () => {
    setRentals([
      ...rentals,
      {
        id: rentals.length + 1,
        startDate: null,
        endDate: null,
        renter: "",
        equipment: [],
        issuer: "",
        receiver: "",
        status: "Бронь",
        notes: "",
        conflicts: "",
      },
    ]);
  };

  const handleDateChange = (rowId, type, date) => {
    let newRentals = [...rentals];
    newRentals[rowId][type] = date;
  
    let conflicts = [];
  
    if (!newRentals[rowId].startDate || !newRentals[rowId].endDate) {
      conflicts.push("Необходимо указать обе даты");
    } 
    if (newRentals[rowId].startDate > newRentals[rowId].endDate) {
      conflicts.push("Дата окончания не может быть раньше даты начала");
    } 
    if (
      (newRentals[rowId].status === "Выдано" || newRentals[rowId].status === "Возвращено") &&
      newRentals[rowId].startDate > new Date()
    ) {
      conflicts.push("Статус 'Выдано' или 'Возвращено', но дата начала аренды в будущем");
    }
  
    newRentals[rowId].conflicts = conflicts.join(" ; ");
    setRentals(newRentals);
  };

  return (
    <div className="table-container">
      <button onClick={addRental}>+ Добавить аренду</button>
      <table>
        <thead>
          <tr>
            {["Дата аренды", "Кто берет", "Оборудование", "Кто выдал", "Кто принял", "Статус", "Примечания", "Конфликты"].map((header, index) => (
              <th
                key={index}
                style={{ width: columnWidths[index] || "auto" }}
                className={debugMode ? "resizable" : ""}
                onMouseDown={(e) => {
                  if (!debugMode) return;
                  const startX = e.clientX;
                  const startWidth = columnWidths[index] || e.target.offsetWidth;

                  const onMouseMove = (e) => {
                    const newWidth = Math.max(50, startWidth + e.clientX - startX);
                    saveColumnWidth(index, newWidth);
                  };

                  const onMouseUp = () => {
                    document.removeEventListener("mousemove", onMouseMove);
                    document.removeEventListener("mouseup", onMouseUp);
                  };

                  document.addEventListener("mousemove", onMouseMove);
                  document.addEventListener("mouseup", onMouseUp);
                }}
              >
                {header}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {rentals.map((rental, rowIndex) => (
            <tr
              key={rowIndex}
              style={{ height: rowHeights[rowIndex] || "auto" }}
              className={debugMode ? "resizable-row" : ""}
              onMouseDown={(e) => {
                if (!debugMode) return;
                const startY = e.clientY;
                const startHeight = rowHeights[rowIndex] || e.target.offsetHeight;

                const onMouseMove = (e) => {
                  const newHeight = Math.max(20, startHeight + e.clientY - startY);
                  saveRowHeight(rowIndex, newHeight);
                };

                const onMouseUp = () => {
                  document.removeEventListener("mousemove", onMouseMove);
                  document.removeEventListener("mouseup", onMouseUp);
                };

                document.addEventListener("mousemove", onMouseMove);
                document.addEventListener("mouseup", onMouseUp);
              }}
            >
              <td className="date-cell">
                <div className="date-block">
                  <span>С</span>
                  <DatePicker
                    selected={rental.startDate}
                    onChange={(date) => handleDateChange(rowIndex, "startDate", date)}
                    showTimeSelect
                    dateFormat="dd/MM/yy HH:mm"
                    timeFormat="HH:mm"
                    timeIntervals={15}
                    placeholderText="Выбрать"
                    className="date-picker"
                  />
                </div>
                <div className="date-block">
                  <span>По</span>
                  <DatePicker
                    selected={rental.endDate}
                    onChange={(date) => handleDateChange(rowIndex, "endDate", date)}
                    showTimeSelect
                    dateFormat="dd/MM/yy HH:mm"
                    timeFormat="HH:mm"
                    timeIntervals={15}
                    placeholderText="Выбрать"
                    className="date-picker"
                  />
                </div>
              </td>
              <td><input type="text" value={rental.renter} onChange={(e) => {
                let newRentals = [...rentals];
                newRentals[rowIndex].renter = e.target.value;
                setRentals(newRentals);
              }} /></td>
              <td onClick={() => setSelectedRow(rowIndex)}>
                {rental.equipment.length > 0 ? rental.equipment.join(", ") : "Выбрать"}
              </td>
              <td><input type="text" value={rental.issuer} onChange={(e) => {
                let newRentals = [...rentals];
                newRentals[rowIndex].issuer = e.target.value;
                setRentals(newRentals);
              }} /></td>
              <td><input type="text" value={rental.receiver} onChange={(e) => {
                let newRentals = [...rentals];
                newRentals[rowIndex].receiver = e.target.value;
                setRentals(newRentals);
              }} /></td>
              <td><StatusSelect status={rental.status} onChange={(status) => {
                let newRentals = [...rentals];
                newRentals[rowIndex].status = status;
                setRentals(newRentals);
              }} /></td>
              <td><input type="text" value={rental.notes} onChange={(e) => {
                let newRentals = [...rentals];
                newRentals[rowIndex].notes = e.target.value;
                setRentals(newRentals);
              }} /></td>
              <td>{rental.conflicts}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Table;
