import React, { useState, useEffect } from "react";
import RentalRow from "./RentalRow";

const Table = () => {
    const [bookings, setBookings] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchBookings();
    }, []);

    const fetchBookings = async () => {
        try {
            const response = await fetch("/api/bookings");
            if (!response.ok) {
                if (response.status === 404) {
                    throw new Error("Resource not found (404)");
                } else if (response.status === 500) {
                    throw new Error("Internal server error (500)");
                } else {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
            }
            const data = await response.json();
            setBookings(Array.isArray(data) ? data : []);
        } catch (err) {
            console.error("Error loading data:", err);
            setError(err.message);
        }
    };

    const addRental = () => {
        const newRental = {
            id: Date.now(), // Временный ID для нового бронирования
            start_date: "",
            end_date: "",
            renter: "",
            equipment: [],  // Изменено на массив для поддержки множественного выбора оборудования
            issuer: "",
            receiver: "",
            status: "Бронь",
            notes: ""
        };
        setBookings(prevBookings => [...prevBookings, newRental]);
    };

    const updateRental = async (id, updatedRental) => {
        try {
            const response = await fetch(`/api/bookings/${id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(updatedRental)
            });

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            setBookings(prevBookings => 
                prevBookings.map(booking => 
                    booking.id === id ? updatedRental : booking
                )
            );
        } catch (error) {
            console.error("Ошибка обновления:", error);
            setError("Не удалось обновить бронирование");
        }
    };

    const deleteRental = async (id) => {
        if (!id) {
            console.error("ID не указан для удаления");
            return;
        }

        try {
            const response = await fetch(`/api/bookings/${id}`, {
                method: 'DELETE',
                headers: {
                    'Content-Type': 'application/json',
                }
            });

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            // Немедленно обновляем состояние после успешного удаления
            setBookings(prevBookings => 
                prevBookings.filter(booking => booking.id !== id)
            );
        } catch (error) {
            console.error("Ошибка удаления:", error);
            setError("Не удалось удалить бронирование");
        }
    };

    return (
        <div>
            <h2>📋 Таблица аренды</h2>
            {error && <p>Ошибка: {error}</p>}
            <button onClick={addRental}>➕ Добавить аренду</button>
            <table>
                <thead>
                    <tr>
                        <th>Дата аренды</th>
                        <th>Имя</th>
                        <th>Кто выдал</th>
                        <th>Кто принял</th>
                        <th>Оборудование</th>
                        <th>Примечания</th>
                        <th>Статус</th>
                        <th>Конфликты</th>
                        <th>Действия</th>
                    </tr>
                </thead>
                <tbody>
                    {bookings.map((booking) => (
                        <RentalRow 
                            key={booking.id}
                            booking={booking}
                            onUpdate={updateRental}
                            onDelete={() => deleteRental(booking.id)}
                        />
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Table;
