import { useState, useEffect } from "react";

function useRentals() {
  const [rentals, setRentals] = useState([]);
  const [equipmentList, setEquipmentList] = useState([]);
  const [selectedRow, setSelectedRow] = useState(null);

  useEffect(() => {
    console.time("Загрузка оборудования");
    fetch("/api/inventory")
      .then((res) => res.json())
      .then((data) => {
        console.timeEnd("Загрузка оборудования");
        setEquipmentList(data);
      })
      .catch((err) => console.error("Ошибка загрузки оборудования:", err));
  }, []);

  const addRental = () => {
    setRentals([
      ...rentals,
      {
        id: rentals.length + 1,
        startDate: null,
        endDate: null,
        renter: "",
        equipment: [],
        issuer: "",
        receiver: "",
        status: "Бронь",
        notes: "",
        conflicts: "",
      },
    ]);
  };

  const updateEquipment = (rowIndex, selectedEquipment) => {
    let newRentals = [...rentals];
    newRentals[rowIndex].equipment = selectedEquipment;
    setRentals(newRentals);
  };

  return { rentals, addRental, updateEquipment, equipmentList, selectedRow, setSelectedRow };
}

export default useRentals;
