// updateEquipment.js
export async function updateEquipment(rowIndex, selectedEquipment, rentals, setRentals) {
    try {
      const response = await fetch("/api/update-equipment", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          rentalId: rentals[rowIndex].id,
          equipment: selectedEquipment,
        }),
      });
  
      const result = await response.json();
      if (!response.ok) throw new Error(result.error || "Ошибка обновления");
  
      let newRentals = [...rentals];
      newRentals[rowIndex].equipment = selectedEquipment;
      setRentals(newRentals);
    } catch (error) {
      console.error("Ошибка обновления оборудования:", error);
      alert("Ошибка обновления оборудования. Попробуйте снова.");
    }
  }
