import React, { useState } from "react";
import EquipmentList from "./EquipmentList";
import StatusSelect from "./StatusSelect";

const RentalRow = ({ booking, onUpdate = () => {}, onDelete = () => {} }) => {
    const [rental, setRental] = useState(booking);
    const [showEquipmentList, setShowEquipmentList] = useState(false);

    const handleChange = (e) => {
        const updatedRental = { ...rental, [e.target.name]: e.target.value };
        setRental(updatedRental);
    };

    const handleBlur = () => {
        onUpdate(rental.id, rental);
    };

    const updateEquipmentQuantity = (equipmentId, quantity) => {
        const updatedEquipment = rental.equipment.map(eq => 
            eq.id === equipmentId ? { ...eq, quantity: Math.max(1, quantity) } : eq
        );
        const updatedRental = { ...rental, equipment: updatedEquipment };
        setRental(updatedRental);
        onUpdate(rental.id, updatedRental);
    };

    return (
        <>
            <tr>
                <td>
                    <input
                        type="datetime-local"
                        name="start_date"
                        value={rental.start_date ? rental.start_date.slice(0, 16) : ""}
                        onChange={handleChange}
                        onBlur={handleBlur}
                    />
                    {" - "}
                    <input
                        type="datetime-local"
                        name="end_date"
                        value={rental.end_date ? rental.end_date.slice(0, 16) : ""}
                        onChange={handleChange}
                        onBlur={handleBlur}
                    />
                </td>

                <td><input type="text" name="renter" value={rental.renter} onChange={handleChange} onBlur={handleBlur} /></td>
                <td><input type="text" name="issuer" value={rental.issuer} onChange={handleChange} onBlur={handleBlur} /></td>
                <td><input type="text" name="receiver" value={rental.receiver} onChange={handleChange} onBlur={handleBlur} /></td>

                <td>
                    <button onClick={() => setShowEquipmentList(true)} autoFocus>
                        {rental.equipment && rental.equipment.length > 0
                            ? rental.equipment.map((eq) => (
                                <div key={eq.id} style={{ display: "flex", alignItems: "center", marginBottom: "4px" }}>
                                    <div style={{ display: "flex", alignItems: "center", marginRight: "8px" }}>
                                        <button 
                                            onClick={(e) => {
                                                e.stopPropagation();
                                                updateEquipmentQuantity(eq.id, (eq.quantity || 1) - 1);
                                            }}
                                            style={{ padding: "2px 6px" }}
                                        >
                                            -
                                        </button>
                                        <input
                                            type="number"
                                            value={eq.quantity || 1}
                                            min="1"
                                            onChange={(e) => {
                                                e.stopPropagation();
                                                updateEquipmentQuantity(eq.id, parseInt(e.target.value) || 1);
                                            }}
                                            onClick={(e) => e.stopPropagation()}
                                            style={{ 
                                                width: "40px", 
                                                textAlign: "center",
                                                margin: "0 4px"
                                            }}
                                        />
                                        <button 
                                            onClick={(e) => {
                                                e.stopPropagation();
                                                updateEquipmentQuantity(eq.id, (eq.quantity || 1) + 1);
                                            }}
                                            style={{ padding: "2px 6px" }}
                                        >
                                            +
                                        </button>
                                    </div>
                                    <span
                                        style={{
                                            display: "inline-block",
                                            backgroundColor: "green",
                                            color: "white",
                                            padding: "5px",
                                            borderRadius: "5px",
                                        }}
                                    >
                                        {eq.name} × {eq.quantity || 1}
                                    </span>
                                </div>
                              ))
                            : "Выбрать оборудование"}
                    </button>
                </td>

                <td><input type="text" name="notes" value={rental.notes} onChange={handleChange} onBlur={handleBlur} /></td>

                <td>
                    <StatusSelect
                        value={rental.status}
                        onChange={(status) => {
                            const updatedRental = { ...rental, status };
                            setRental(updatedRental);
                            onUpdate(rental.id, updatedRental);
                        }}
                    />
                </td>

                <td>⚠️</td>

                <td>
                    <button onClick={() => onDelete(rental.id)}>❌</button>
                </td>
            </tr>

            {showEquipmentList && (
                <EquipmentList
                    onSelect={(selectedEquipment) => {
                        // Добавляем начальное количество для каждого выбранного оборудования
                        const equipmentWithQuantity = selectedEquipment.map(eq => ({
                            ...eq,
                            quantity: 1
                        }));
                        const updatedRental = { ...rental, equipment: equipmentWithQuantity };
                        setRental(updatedRental);
                        onUpdate(rental.id, updatedRental);
                        setShowEquipmentList(false);
                    }}
                    onClose={() => setShowEquipmentList(false)}
                />
            )}
        </>
    );
};

export default RentalRow;
