import React, { useState } from "react";
import Calendar from "./Calendar";
import EquipmentList from "./EquipmentList";
import "./styles.css";


const Sidebar = ({ isOpen, toggleSidebar }) => {
  const [activePopup, setActivePopup] = useState(null); // Управляет открытыми окнами
  const popupRef = useRef(null);

  // Закрытие при клике вне окна
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (popupRef.current && !popupRef.current.contains(event.target)) {
        setActivePopup(null);
      }
    };

    if (activePopup) {
      document.addEventListener("mousedown", handleClickOutside);
    } else {
      document.removeEventListener("mousedown", handleClickOutside);
    }

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [activePopup]);

  return (
    <div className={`sidebar ${isOpen ? "open" : ""}`}>
      <button className="sidebar-toggle" onClick={toggleSidebar}>
        {isOpen ? "<<" : ">>"}
      </button>
      <h2>Инвентарь</h2>

      {/* Кнопка "Дата аренды" */}
      <button
        className="sidebar-button"
        onClick={(e) => {
          e.stopPropagation(); // предотвращает всплытие
          setActivePopup(activePopup === "date" ? null : "date");
        }}
      >
        Дата аренды
      </button>

      {/* Всплывающее окно для выбора даты */}
      {activePopup === "date" && (
        <div className="popup" ref={popupRef}>
          <button className="close-btn" onClick={() => setActivePopup(null)}>×</button>
          <p>Выберите дату аренды</p>
          {/* Тут будет календарь */}
        </div>
      )}

      {/* Кнопка "Инвентарь" */}
      <button
        className="sidebar-button"
        onClick={(e) => {
          e.stopPropagation();
          setActivePopup(activePopup === "inventory" ? null : "inventory");
        }}
      >
        Инвентарь
      </button>

      {/* Всплывающее окно для выбора инвентаря */}
      {activePopup === "inventory" && (
        <div className="popup" ref={popupRef}>
          <button className="close-btn" onClick={() => setActivePopup(null)}>×</button>
          <p>Выберите инвентарь</p>
          {/* Тут будет список оборудования */}
        </div>
      )}
    </div>
  );
};

export default Sidebar;
